#!/usr/bin/env node

const fs = require('fs');
const path = require('path');

const SRC_DIR = path.join(__dirname, '../src');

// Fun√ß√£o para buscar arquivos .js recursivamente
function findJSFiles(dir) {
  const files = [];
  const items = fs.readdirSync(dir);

  for (const item of items) {
    const fullPath = path.join(dir, item);
    const stat = fs.statSync(fullPath);

    if (stat.isDirectory()) {
      files.push(...findJSFiles(fullPath));
    } else if (item.endsWith('.js')) {
      files.push(fullPath);
    }
  }

  return files;
}

// Mapeamento de componentes Paper para Elements
const componentMappings = {
  'FAB': {
    import: "import { FAB } from 'react-native-elements';",
    replacement: 'FAB'
  },
  'Snackbar': {
    import: "// Snackbar substitu√≠do por notifica√ß√£o customizada",
    replacement: 'View // TODO: Implementar notifica√ß√£o customizada'
  },
  'Switch': {
    import: "import { Switch } from 'react-native';",
    replacement: 'Switch'
  },
  'Surface': {
    import: "import { Card } from 'react-native-elements';",
    replacement: 'Card'
  },
  'Chip': {
    import: "import { Badge } from 'react-native-elements';",
    replacement: 'Badge'
  },
  'Divider': {
    import: "import { Divider } from 'react-native-elements';",
    replacement: 'Divider'
  }
};

// Fun√ß√£o para corrigir keys em .map() cr√≠ticos
function fixCriticalMapKeys(content, filePath) {
  let fixed = false;

  // Casos espec√≠ficos identificados pelo script anterior
  const fixes = [
    // StudentEvolution.js - linha 290
    {
      pattern: /stats\.modalities\.map\(\(modality, index\) => \(/,
      replacement: 'stats.modalities.map((modality, index) => (',
      keyFix: true
    },
    // StudentEvolution.js - linha 305  
    {
      pattern: /stats\.recentGraduations\.map\(\(graduation, index\) => \(/,
      replacement: 'stats.recentGraduations.map((graduation, index) => (',
      keyFix: true
    }
  ];

  for (const fix of fixes) {
    if (fix.pattern.test(content)) {
      // Verificar se j√° tem key
      const lines = content.split('\n');
      for (let i = 0; i < lines.length; i++) {
        if (fix.pattern.test(lines[i])) {
          // Procurar por key nas pr√≥ximas 3 linhas
          let hasKey = false;
          for (let j = 1; j <= 3 && i + j < lines.length; j++) {
            if (lines[i + j].includes('key=')) {
              hasKey = true;
              break;
            }
          }

          if (!hasKey) {
            console.log(`‚ö†Ô∏è  Problema de key encontrado em ${path.relative(process.cwd(), filePath)}:${i + 1}`);
            console.log(`   ${lines[i].trim()}`);
          }
        }
      }
    }
  }

  return { content, fixed };
}

// Fun√ß√£o para migrar componentes Paper
function migratePaperComponents(content, filePath) {
  let modified = content;
  let changes = [];

  // Substituir componentes espec√≠ficos
  const replacements = [
    // FAB
    {
      pattern: /<FAB\s+([^>]*)\s*\/?>[\s\S]*?<\/FAB>/g,
      replacement: (match, props) => {
        changes.push('FAB ‚Üí FAB (react-native-elements)');
        return match; // Manter FAB do react-native-elements
      }
    },

    // Surface ‚Üí Card
    {
      pattern: /<Surface\s+([^>]*?)>/g,
      replacement: (match, props) => {
        changes.push('Surface ‚Üí Card');
        return `<Card containerStyle={${props.includes('style=') ? props.replace('style=', '') : 'styles.card'}}>`;
      }
    },
    {
      pattern: /<\/Surface>/g,
      replacement: () => {
        return '</Card>';
      }
    },

    // Chip ‚Üí Badge
    {
      pattern: /<Chip\s+([^>]*?)>/g,
      replacement: (match, props) => {
        changes.push('Chip ‚Üí Badge');
        // Extrair propriedades relevantes
        const modeMatch = props.match(/mode="([^"]*)"/) || [];
        const styleMatch = props.match(/style=\{([^}]*)\}/) || [];

        let badgeProps = '';
        if (styleMatch[1]) {
          badgeProps += `badgeStyle={${styleMatch[1]}} `;
        }

        return `<Badge ${badgeProps}${props.replace(/mode="[^"]*"/, '').trim()}>`;
      }
    },
    {
      pattern: /<\/Chip>/g,
      replacement: () => '</Badge>'
    },

    // Divider j√° √© compat√≠vel, apenas garantir import correto

    // Switch j√° √© do React Native core
  ];

  for (const { pattern, replacement } of replacements) {
    if (pattern.test(modified)) {
      modified = modified.replace(pattern, replacement);
    }
  }

  return { content: modified, changes };
}

// Fun√ß√£o principal
function main() {
  console.log('üîß Corrigindo keys em .map() e migrando componentes Paper...\n');

  const jsFiles = findJSFiles(SRC_DIR);
  let totalFixed = 0;
  let totalMigrated = 0;

  for (const filePath of jsFiles) {
    const relativePath = path.relative(process.cwd(), filePath);
    let content = fs.readFileSync(filePath, 'utf8');
    const originalContent = content;

    // Corrigir keys em .map()
    const keyResult = fixCriticalMapKeys(content, filePath);
    content = keyResult.content;

    // Migrar componentes Paper
    const migrationResult = migratePaperComponents(content, filePath);
    content = migrationResult.content;

    // Salvar se houve mudan√ßas
    if (content !== originalContent) {
      fs.writeFileSync(filePath, content, 'utf8');
      console.log(`‚úÖ ${relativePath}`);

      if (keyResult.fixed) {
        console.log(`   üîë Keys corrigidas`);
        totalFixed++;
      }

      if (migrationResult.changes.length > 0) {
        console.log(`   üì¶ Migra√ß√µes: ${migrationResult.changes.join(', ')}`);
        totalMigrated++;
      }
    }
  }

  console.log('\n' + '='.repeat(60));
  console.log('üìä RESUMO DAS CORRE√á√ïES');
  console.log('='.repeat(60));
  console.log(`üîë Arquivos com keys corrigidas: ${totalFixed}`);
  console.log(`üì¶ Arquivos com componentes migrados: ${totalMigrated}`);

  console.log('\nüìã PR√ìXIMOS PASSOS:');
  console.log('1. Verificar imports dos componentes migrados');
  console.log('2. Testar funcionalidade dos componentes migrados');
  console.log('3. Executar app no navegador para validar corre√ß√µes');
}

if (require.main === module) {
  main();
}

scripts/fix-react-native-web-issues.js.content:
#!/usr/bin/env node

const fs = require('fs');
const path = require('path');

// Configura√ß√£o dos diret√≥rios a serem processados
const SRC_DIR = path.join(__dirname, '../src');

// Fun√ß√£o para buscar arquivos .js recursivamente
function findJSFiles(dir) {
  const files = [];
  const items = fs.readdirSync(dir);

  for (const item of items) {
    const fullPath = path.join(dir, item);
    const stat = fs.statSync(fullPath);

    if (stat.isDirectory()) {
      files.push(...findJSFiles(fullPath));
    } else if (item.endsWith('.js')) {
      files.push(fullPath);
    }
  }

  return files;
}

// Fun√ß√£o para corrigir boxShadow
function fixBoxShadow(content) {
  // Padr√£o para encontrar boxShadow standalone (n√£o dentro de Platform.select)
  const standaloneBoxShadowRegex = /(\s+)boxShadow:\s*['"`]([^'"`]+)['"`],?\s*\n/g;

  return content.replace(standaloneBoxShadowRegex, (match, indent, shadowValue) => {
    return `${indent}...Platform.select({
${indent}  ios: {
${indent}    shadowColor: '#000',
${indent}    shadowOffset: { width: 0, height: 2 },
${indent}    shadowOpacity: 0.1,
${indent}    shadowRadius: 4,
${indent}  },
${indent}  android: {
${indent}    elevation: 4,
${indent}  },
${indent}  web: {
${indent}    boxShadow: '${shadowValue}',
${indent}  },
${indent}}),
`;
  });
}

// Fun√ß√£o para verificar e corrigir keys em .map()
function fixMapKeys(content, filePath) {
  const issues = [];
  const lines = content.split('\n');

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];

    // Buscar por .map( seguido de uma fun√ß√£o
    if (line.includes('.map(') && (line.includes('(') || line.includes('=>'))) {
      // Verificar se h√° uma key nas pr√≥ximas linhas
      let hasKey = false;
      let checkLines = Math.min(5, lines.length - i - 1);

      for (let j = 1; j <= checkLines; j++) {
        if (lines[i + j] && lines[i + j].includes('key=')) {
          hasKey = true;
          break;
        }
      }

      if (!hasKey) {
        issues.push({
          file: filePath,
          line: i + 1,
          content: line.trim(),
          suggestion: 'Adicionar key prop √∫nica no elemento retornado pelo .map()'
        });
      }
    }
  }

  return issues;
}

// Fun√ß√£o para encontrar componentes do react-native-paper restantes
function findPaperComponents(content, filePath) {
  const paperComponents = [
    'Appbar', 'FAB', 'Portal', 'Modal', 'Dialog', 'Snackbar', 
    'Surface', 'Divider', 'Chip', 'DataTable', 'Menu',
    'ProgressBar', 'RadioButton', 'Switch', 'ToggleButton'
  ];

  const issues = [];
  const lines = content.split('\n');

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];

    for (const component of paperComponents) {
      if (line.includes(`<${component}`) || line.includes(`${component}.`)) {
        issues.push({
          file: filePath,
          line: i + 1,
          content: line.trim(),
          component: component,
          suggestion: `Substituir ${component} por componente equivalente do react-native-elements`
        });
      }
    }
  }

  return issues;
}

// Fun√ß√£o principal
function main() {
  console.log('üîß Iniciando corre√ß√£o autom√°tica dos problemas React Native Web...\n');

  const jsFiles = findJSFiles(SRC_DIR);
  let totalFixed = 0;
  let mapIssues = [];
  let paperIssues = [];

  console.log(`üìÅ Encontrados ${jsFiles.length} arquivos .js para processar\n`);

  // Processar cada arquivo
  for (const filePath of jsFiles) {
    const relativePath = path.relative(process.cwd(), filePath);
    console.log(`üìÑ Processando: ${relativePath}`);

    let content = fs.readFileSync(filePath, 'utf8');
    const originalContent = content;

    // Corrigir boxShadow
    content = fixBoxShadow(content);

    // Verificar problemas de .map() keys
    const mapProblems = fixMapKeys(content, relativePath);
    mapIssues.push(...mapProblems);

    // Verificar componentes react-native-paper restantes
    const paperProblems = findPaperComponents(content, relativePath);
    paperIssues.push(...paperProblems);

    // Salvar arquivo se houve mudan√ßas
    if (content !== originalContent) {
      fs.writeFileSync(filePath, content, 'utf8');
      totalFixed++;
      console.log(`  ‚úÖ Corrigido boxShadow`);
    } else {
      console.log(`  ‚ö™ Nenhuma corre√ß√£o necess√°ria`);
    }
  }

  // Relat√≥rio final
  console.log('\n' + '='.repeat(60));
  console.log('üìä RELAT√ìRIO DE CORRE√á√ïES');
  console.log('='.repeat(60));
  console.log(`‚úÖ Arquivos com boxShadow corrigidos: ${totalFixed}`);

  // Relat√≥rio de problemas de .map() keys
  if (mapIssues.length > 0) {
    console.log(`\n‚ö†Ô∏è  PROBLEMAS DE KEYS EM .map() (${mapIssues.length}):`);
    console.log('-'.repeat(40));

    mapIssues.forEach(issue => {
      console.log(`üìÅ ${issue.file}:${issue.line}`);
      console.log(`   ${issue.content}`);
      console.log(`   üí° ${issue.suggestion}\n`);
    });
  } else {
    console.log(`‚úÖ Nenhum problema de key em .map() encontrado`);
  }

  // Relat√≥rio de componentes react-native-paper
  if (paperIssues.length > 0) {
    console.log(`\n‚ö†Ô∏è  COMPONENTES REACT-NATIVE-PAPER RESTANTES (${paperIssues.length}):`);
    console.log('-'.repeat(40));

    // Agrupar por componente
