    console.log('1. Aplicar regras de seguran√ßa: firebase deploy --only firestore:rules');
    console.log('2. Criar √≠ndices: firebase deploy --only firestore:indexes');
    console.log('3. Testar o app com os dados criados');

  } catch (error) {
    console.error('‚ùå Erro ao criar dados:', error);
    process.exit(1);
  }
}

// Executar
createInitialData().then(() => {
