import React from 'react';
import { Animated, TouchableOpacity, Platform } from 'react-native';
import { Button } from 'react-native-paper';
import { ShadowUtils } from '../utils/animations';

const AnimatedButton = ({ 
  children, 
  style, 
  onPress,
  mode = 'contained',
  disabled = false,
  loading = false,
  elevation = 'light',
  ...props 
}) => {
  const scaleValue = React.useRef(new Animated.Value(1)).current;
  const fadeValue = React.useRef(new Animated.Value(1)).current;

  const handlePressIn = () => {
    if (disabled || loading) return;

    Animated.parallel([
      Animated.spring(scaleValue, {
        toValue: 0.95,
        useNativeDriver: Platform.OS !== 'web',
        tension: 300,
        friction: 10,
      }),
      Animated.timing(fadeValue, {
        toValue: 0.8,
        duration: 100,
        useNativeDriver: Platform.OS !== 'web',
      }),
    ]).start();
  };

  const handlePressOut = () => {
    if (disabled || loading) return;

    Animated.parallel([
      Animated.spring(scaleValue, {
        toValue: 1,
        useNativeDriver: Platform.OS !== 'web',
        tension: 300,
        friction: 10,
      }),
      Animated.timing(fadeValue, {
        toValue: 1,
        duration: 100,
        useNativeDriver: Platform.OS !== 'web',
      }),
    ]).start();
  };

  const shadowStyle = ShadowUtils[elevation] || ShadowUtils.light;

  return (
    <Animated.View 
      style={[
        {
          transform: [{ scale: scaleValue }],
          opacity: fadeValue,
        },
        mode === 'contained' ? shadowStyle : {},
      ]}
    >
      <Button
        mode={mode}
        onPress={onPress}
        disabled={disabled}
        loading={loading}
        onPressIn={handlePressIn}
        onPressOut={handlePressOut}
        style={[
          {
            borderRadius: 8,
            marginVertical: 4,
            ...(Platform.OS === 'web' && { pointerEvents: disabled ? 'none' : 'auto' }),
          },
          style
        ]}
        {...props}
      >
        {children}
      </Button>
    </Animated.View>
  );
};

export default AnimatedButton;

src/components/AnimatedCard.js.content:
import React from 'react';
import { Animated, Platform } from 'react-native';
import { Card } from 'react-native-paper';
import { ShadowUtils } from '../utils/animations';

const AnimatedCard = ({
  children,
  style,
  elevation = 'light',
  animationType = 'fadeIn',
  delay = 0,
  ...props
}) => {
  const animatedValue = React.useRef(new Animated.Value(0)).current;

  React.useEffect(() => {
    Animated.timing(animatedValue, {
      toValue: 1,
      duration: 300,
      delay,
      useNativeDriver: Platform.OS !== 'web',
    }).start();
  }, [delay]);

  const getAnimationStyle = () => {
    switch (animationType) {
      case 'fadeIn':
        return {
          opacity: animatedValue,
          transform: [{
            translateY: animatedValue.interpolate({
              inputRange: [0, 1],
              outputRange: [20, 0],
            }),
          }],
        };
      case 'scaleIn':
        return {
          opacity: animatedValue,
          transform: [{
            scale: animatedValue.interpolate({
              inputRange: [0, 1],
              outputRange: [0.9, 1],
            }),
          }],
        };
      case 'slideInRight':
        return {
          opacity: animatedValue,
          transform: [{
            translateX: animatedValue.interpolate({
              inputRange: [0, 1],
              outputRange: [30, 0],
            }),
          }],
        };
      default:
        return { opacity: animatedValue };
    }
  };

  const shadowStyle = ShadowUtils[elevation] || ShadowUtils.light;

  return (
    <Animated.View style={[getAnimationStyle()]}>
      <Card
        style={[
          shadowStyle,
          {
            margin: 8,
            borderRadius: 12,
          },
          style
        ]}
        {...props}
      >
        {children}
      </Card>
    </Animated.View>
  );
};

export default AnimatedCard;

