import React, { useState } from 'react';
import { View, StyleSheet, Alert } from 'react-native';
import { Button, Text, Card, ActivityIndicator } from 'react-native-paper';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import locationService from '../services/locationService';
import { firestoreService } from '../services/firestoreService';
import { useAuth } from '../contexts/AuthContext';

const CheckInButton = ({ classId, className, onCheckInSuccess }) => {
  const [loading, setLoading] = useState(false);
  const [locationStatus, setLocationStatus] = useState(null);
  const { user } = useAuth();

  const handleCheckIn = async () => {
    if (!user || !classId) {
      Alert.alert('Erro', 'Dados de usu√°rio ou aula n√£o encontrados');
      return;
    }

    setLoading(true);
    setLocationStatus('Verificando localiza√ß√£o...');

    try {
      // Validar localiza√ß√£o
      const locationValidation = await locationService.validateCheckIn(classId);

      if (!locationValidation.success) {
        setLocationStatus('Localiza√ß√£o inv√°lida');
        Alert.alert(
          'Check-in Negado',
          locationValidation.reason,
          [{ text: 'OK', onPress: () => setLocationStatus(null) }]
        );
        return;
      }

      setLocationStatus('Registrando presen√ßa...');

      // Criar registro de check-in
      const checkInData = {
        studentId: user.uid,
        classId: classId,
        date: new Date(),
        status: 'present',
        location: {
          latitude: locationValidation.location.latitude,
          longitude: locationValidation.location.longitude,
          accuracy: locationValidation.location.accuracy,
          distance: locationValidation.distance
        },
        timestamp: locationValidation.timestamp,
        createdAt: new Date(),
        updatedAt: new Date()
      };

      await firestoreService.addDocument('checkins', checkInData);

      setLocationStatus('Check-in realizado!');

      Alert.alert(
        'Sucesso! ‚úÖ',
        `Check-in realizado na aula de ${className}!\n\nDist√¢ncia da academia: ${locationValidation.distance}m`,
        [
          {
            text: 'OK',
            onPress: () => {
              setLocationStatus(null);
              if (onCheckInSuccess) {
                onCheckInSuccess(checkInData);
              }
            }
          }
        ]
      );

    } catch (error) {
      console.error('Erro no check-in:', error);
      setLocationStatus('Erro no check-in');

      Alert.alert(
        'Erro',
        'N√£o foi poss√≠vel realizar o check-in. Tente novamente.',
        [{ text: 'OK', onPress: () => setLocationStatus(null) }]
      );
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = () => {
    if (locationStatus?.includes('inv√°lida')) return '#F44336';
    if (locationStatus?.includes('realizado')) return '#4CAF50';
    return '#FF9800';
  };

  return (
    <Card style={styles.container}>
      <Card.Content>
        <View style={styles.header}>
          <MaterialCommunityIcons 
            name="map-marker-check" 
            size={24} 
            color="#2196F3" 
          />
          <Text style={styles.title}>Check-in com Localiza√ß√£o</Text>
        </View>

        {locationStatus && (
          <View style={[styles.statusContainer, { backgroundColor: getStatusColor() + '20' }]}>
            <MaterialCommunityIcons 
              name="information" 
              size={16} 
              color={getStatusColor()} 
            />
            <Text style={[styles.statusText, { color: getStatusColor() }]}>
              {locationStatus}
            </Text>
          </View>
        )}

        <Text style={styles.description}>
          Voc√™ precisa estar na academia para fazer check-in
        </Text>

        <Button
          mode="contained"
          onPress={handleCheckIn}
          disabled={loading}
          style={styles.button}
          icon={loading ? undefined : "map-marker-check"}
          contentStyle={styles.buttonContent}
        >
          {loading ? (
            <View style={styles.loadingContainer}>
              <ActivityIndicator color="white" size="small" />
              <Text style={styles.loadingText}>Verificando...</Text>
            </View>
          ) : (
            'Fazer Check-in'
          )}
        </Button>

        <Text style={styles.hint}>
          üí° Certifique-se de estar dentro da academia
        </Text>
      </Card.Content>
    </Card>
  );
};

const styles = StyleSheet.create({
  container: {
    margin: 16,
    elevation: 4,
    borderRadius: 12,
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 12,
  },
  title: {
    fontSize: 18,
    fontWeight: '600',
    marginLeft: 8,
    color: '#333',
  },
  statusContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 8,
    borderRadius: 8,
    marginBottom: 12,
  },
  statusText: {
    marginLeft: 8,
    fontSize: 14,
    fontWeight: '500',
  },
  description: {
    fontSize: 14,
    color: '#666',
    marginBottom: 16,
    textAlign: 'center',
  },
  button: {
    marginVertical: 8,
    borderRadius: 25,
    elevation: 2,
  },
  buttonContent: {
    paddingVertical: 8,
  },
  loadingContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
  },
  loadingText: {
    color: 'white',
    marginLeft: 8,
    fontSize: 16,
  },
  hint: {
