import React, { useState } from 'react';
import { View, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import { 
  IconButton, 
  Badge, 
  Portal, 
  Modal, 
  Card, 
  Title, 
  Paragraph, 
  Text,
  Button,
  Divider,
  List
} from 'react-native-paper';
import { Ionicons } from '@expo/vector-icons';
import { useNotification } from '../contexts/NotificationContext';

const NotificationBell = ({ color = '#fff', size = 24 }) => {
  const { 
    unreadNotifications, 
    unreadCount, 
    markNotificationAsRead, 
    loadUserNotifications,
    pushNotificationsEnabled,
    requestNotificationPermissions
  } = useNotification();

  const [modalVisible, setModalVisible] = useState(false);

  const handleNotificationPress = (notification) => {
    markNotificationAsRead(notification.id);
    // Aqui voc√™ pode adicionar navega√ß√£o baseada no tipo da notifica√ß√£o
    if (notification.data?.screen) {
      // navigation.navigate(notification.data.screen);
    }
  };

  const getNotificationIcon = (type) => {
    switch (type) {
      case 'payment':
        return 'card-outline';
      case 'class':
        return 'school-outline';
      case 'graduation':
        return 'trophy-outline';
      case 'general':
        return 'information-outline';
      default:
        return 'notifications-outline';
    }
  };

  const getNotificationColor = (type) => {
    switch (type) {
      case 'payment':
        return '#f44336';
      case 'class':
        return '#2196f3';
      case 'graduation':
        return '#ff9800';
      case 'general':
        return '#4caf50';
      default:
        return '#757575';
    }
  };

  const formatDate = (date) => {
    if (!date) return '';
    const notificationDate = date.toDate ? date.toDate() : new Date(date);
    const now = new Date();
    const diffInHours = Math.floor((now - notificationDate) / (1000 * 60 * 60));

    if (diffInHours < 1) {
      return 'Agora mesmo';
    } else if (diffInHours < 24) {
      return `${diffInHours}h atr√°s`;
    } else {
      const diffInDays = Math.floor(diffInHours / 24);
      return `${diffInDays}d atr√°s`;
    }
  };

  return (
    <>
      <View style={styles.container}>
        <IconButton
          icon="bell"
          iconColor={color}
          size={size}
          onPress={() => setModalVisible(true)}
        />
        {unreadCount > 0 && (
          <Badge style={styles.badge} size={16}>
            {unreadCount > 99 ? '99+' : unreadCount}
          </Badge>
        )}
      </View>

      <Portal>
        <Modal
          visible={modalVisible}
          onDismiss={() => setModalVisible(false)}
          contentContainerStyle={styles.modalContainer}
        >
          <Card style={styles.modalCard}>
            <Card.Title
              title="Notifica√ß√µes"
              subtitle={`${unreadCount} n√£o lida${unreadCount !== 1 ? 's' : ''}`}
              left={(props) => <Ionicons name="notifications" size={24} color="#2196f3" />}
              right={(props) => (
                <IconButton
                  icon="close"
                  onPress={() => setModalVisible(false)}
                />
              )}
            />

            <Divider />

            {!pushNotificationsEnabled && (
              <Card.Content style={styles.permissionCard}>
                <View style={styles.permissionContent}>
                  <Ionicons name="notifications-off" size={32} color="#ff9800" />
                  <Text style={styles.permissionTitle}>
                    Notifica√ß√µes Desabilitadas
                  </Text>
                  <Text style={styles.permissionText}>
                    Ative as notifica√ß√µes para receber lembretes importantes
                  </Text>
                  <Button
                    mode="contained"
                    onPress={requestNotificationPermissions}
