src/components/ResponsiveContainer.js.content:
import React from 'react';
import { View, ScrollView } from 'react-native';
import { useResponsive } from '../hooks/useResponsive';

const ResponsiveContainer = ({ 
  children, 
  scrollable = false,
  style = {},
  contentContainerStyle = {},
  padding = true,
  ...props 
}) => {
  const { isMobile, isTablet, breakpoint } = useResponsive();

  const getContainerStyle = () => {
    const baseStyle = {
      flex: 1,
      backgroundColor: '#f5f5f5',
    };

    if (padding) {
      baseStyle.paddingHorizontal = isMobile ? 16 : isTablet ? 24 : 32;
      baseStyle.paddingVertical = isMobile ? 16 : 20;
    }

    return [baseStyle, style];
  };

  const getContentStyle = () => {
    const baseContentStyle = {
      maxWidth: isMobile ? '100%' : isTablet ? 768 : 1024,
      alignSelf: 'center',
      width: '100%',
    };

    return [baseContentStyle, contentContainerStyle];
  };

  if (scrollable) {
    return (
      <ScrollView
        style={getContainerStyle()}
        contentContainerStyle={getContentStyle()}
        showsVerticalScrollIndicator={false}
        keyboardShouldPersistTaps="handled"
        {...props}
      >
        {children}
      </ScrollView>
    );
  }

  return (
    <View style={getContainerStyle()} {...props}>
      <View style={getContentStyle()}>
        {children}
      </View>
    </View>
  );
};

export default ResponsiveContainer;

src/components/StudentDisassociationDial.content:
import React, { useState } from 'react';
import { View, StyleSheet, Alert } from 'react-native';
import { 
  Dialog, 
  Portal, 
  Button, 
  Text, 
  TextInput,
  Divider,
  Chip
} from 'react-native-paper';
import { Ionicons } from '@expo/vector-icons';
import { useAuth } from '../contexts/AuthContext';
import { useNotification } from '../contexts/NotificationContext';
import { firestoreService } from '../services/firestoreService';

const StudentDisassociationDialog = ({ visible, onDismiss, student, onSuccess }) => {
  const { user, userProfile, academia } = useAuth();
  const { showSuccess, showError } = useNotification();
  const [reason, setReason] = useState('');
  const [loading, setLoading] = useState(false);

  // Verificar se o usu√°rio tem permiss√£o para desassociar alunos
  const canDisassociateStudent = () => {
    return userProfile?.role === 'admin' || userProfile?.role === 'instructor';
  };

  const handleDisassociation = async () => {
    if (!canDisassociateStudent()) {
      showError('Voc√™ n√£o tem permiss√£o para desassociar alunos');
      return;
    }

    if (!reason.trim()) {
      showError('Por favor, informe o motivo da desassocia√ß√£o');
      return;
    }

    Alert.alert(
      'Confirmar Desassocia√ß√£o',
      `Tem certeza que deseja desassociar ${student?.name} da academia?\n\nEsta a√ß√£o n√£o pode ser desfeita.`,
      [
        { text: 'Cancelar', style: 'cancel' },
        { text: 'Confirmar', style: 'destructive', onPress: performDisassociation }
      ]
    );
  };

  const performDisassociation = async () => {
    try {
      setLoading(true);

      // Atualizar status do usu√°rio para inativo
      await firestoreService.update(`academias/${academia.id}/usuarios`, student.id, {
        status: 'inactive',
        disassociatedAt: new Date(),
        disassociatedBy: user.uid,
        disassociationReason: reason,
        updatedAt: new Date()
      });

      // Cancelar pagamentos pendentes
      const pendingPayments = await firestoreService.getDocuments(
        `academias/${academia.id}/payments`,
        [
          { field: 'userId', operator: '==', value: student.id },
          { field: 'status', operator: '==', value: 'pending' }
        ]
      );

      const cancelPaymentPromises = pendingPayments.map(payment =>
        firestoreService.update(`academias/${academia.id}/payments`, payment.id, {
          status: 'cancelled',
          cancelledAt: new Date(),
          cancelledBy: user.uid,
          cancellationReason: 'Aluno desassociado da academia'
        })
      );

      await Promise.all(cancelPaymentPromises);

      // Registrar log da desassocia√ß√£o
      await firestoreService.create(`academias/${academia.id}/logs`, {
        type: 'student_disassociation',
        userId: student.id,
        performedBy: user.uid,
        performedByName: userProfile.name,
        studentName: student.name,
        reason: reason,
        timestamp: new Date(),
        details: {
          cancelledPayments: pendingPayments.length,
          academiaId: academia.id,
          academiaName: academia.name
        }
      });

      // Notificar administradores sobre a desassocia√ß√£o
      await notifyAdminsAboutDisassociation(student, reason);

      showSuccess(`${student.name} foi desassociado da academia`);
      onSuccess();
      onDismiss();
      setReason('');

    } catch (error) {
      console.error('Erro ao desassociar aluno:', error);
      showError('Erro ao desassociar aluno da academia');
    } finally {
      setLoading(false);
    }
  };

  const notifyAdminsAboutDisassociation = async (student, reason) => {
    try {
      // Buscar todos os administradores
      const admins = await firestoreService.getDocuments(
        `academias/${academia.id}/usuarios`,
        [{ field: 'role', operator: '==', value: 'admin' }]
      );

      // Criar notifica√ß√£o para cada administrador (exceto quem executou a a√ß√£o)
      const notificationPromises = admins
        .filter(admin => admin.id !== user.uid)
        .map(admin => 
          firestoreService.create(`academias/${academia.id}/notifications`, {
            userId: admin.id,
            type: 'student_disassociation',
            title: 'Aluno Desassociado',
            message: `${userProfile.name} desassociou ${student.name} da academia`,
            data: {
              studentId: student.id,
              studentName: student.name,
              performedBy: user.uid,
              performedByName: userProfile.name,
              reason: reason,
              academiaId: academia.id
            },
            read: false,
            priority: 'high'
          })
        );

      await Promise.all(notificationPromises);
    } catch (error) {
      console.error('Erro ao notificar administradores:', error);
    }
  };

  if (!canDisassociateStudent()) {
    return null;
  }

  return (
    <Portal>
      <Dialog visible={visible} onDismiss={onDismiss}>
        <Dialog.Title>
          <View style={styles.titleContainer}>
            <Ionicons name="person-remove-outline" size={24} color="#F44336" />
            <Text style={styles.titleText}>Desassociar Aluno</Text>
          </View>
        </Dialog.Title>

        <Dialog.Content>
          <View style={styles.studentInfo}>
            <Text style={styles.label}>Aluno:</Text>
            <Chip mode="outlined" style={styles.studentChip}>
              {student?.name}
            </Chip>
          </View>

          <View style={styles.studentInfo}>
            <Text style={styles.label}>Email:</Text>
            <Text style={styles.studentEmail}>{student?.email}</Text>
          </View>

          <Divider style={styles.divider} />

          <TextInput
            label="Motivo da desassocia√ß√£o *"
            value={reason}
            onChangeText={setReason}
            mode="outlined"
            multiline
            numberOfLines={3}
            placeholder="Informe o motivo da desassocia√ß√£o (ex: inadimpl√™ncia, solicita√ß√£o do aluno, etc.)"
            style={styles.reasonInput}
          />

          <Text style={styles.warningText}>
            ‚ö†Ô∏è Esta a√ß√£o ir√°:
          </Text>
          <Text style={styles.warningItem}>‚Ä¢ Inativar o aluno na academia</Text>
          <Text style={styles.warningItem}>‚Ä¢ Cancelar pagamentos pendentes</Text>
          <Text style={styles.warningItem}>‚Ä¢ Registrar log da opera√ß√£o</Text>
          <Text style={styles.warningItem}>‚Ä¢ Notificar administradores</Text>
        </Dialog.Content>

        <Dialog.Actions>
          <Button 
            onPress={onDismiss}
            disabled={loading}
          >
            Cancelar
          </Button>
          <Button 
            mode="contained"
            onPress={handleDisassociation}
            loading={loading}
            disabled={loading || !reason.trim()}
            buttonColor="#F44336"
          >
            Desassociar
          </Button>
        </Dialog.Actions>
      </Dialog>
    </Portal>
  );
};

const styles = StyleSheet.create({
  titleContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  titleText: {
    marginLeft: 8,
    fontSize: 18,
    fontWeight: 'bold',
    color: '#F44336',
  },
  studentInfo: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 8,
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    marginRight: 8,
    minWidth: 60,
  },
  studentChip: {
    borderColor: '#2196F3',
  },
  studentEmail: {
    fontSize: 16,
    color: '#666',
  },
  divider: {
    marginVertical: 16,
  },
  reasonInput: {
    marginBottom: 16,
  },
  warningText: {
    fontSize: 14,
    fontWeight: 'bold',
    color: '#F44336',
    marginBottom: 8,
  },
  warningItem: {
    fontSize: 14,
    color: '#666',
    marginLeft: 16,
    marginBottom: 4,
  },
});

export default StudentDisassociationDialog;

src/components/UniversalHeader.js.content:
import React from 'react';
import { View, StyleSheet, Alert, TouchableOpacity, Platform } from 'react-native';
import { Appbar, Avatar, Menu, Divider, Modal, Portal, Button, Text } from 'react-native-paper';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { useAuth } from '../contexts/AuthContext';
import { ResponsiveUtils } from '../utils/animations';
import NotificationBell from './NotificationBell';

const UniversalHeader = ({ 
  title, 
  subtitle, 
  navigation, 
  showBack = false,
  showMenu = true,
  backgroundColor = '#4CAF50'
}) => {
  const { user, userProfile, logout } = useAuth();
  const [menuVisible, setMenuVisible] = React.useState(false);
  const [logoutModalVisible, setLogoutModalVisible] = React.useState(false);

  const openMenu = () => {
    console.log('üîê Avatar clicado - abrindo menu');
    setMenuVisible(true);
  };
  const closeMenu = () => {
    console.log('üîê Fechando menu');
    setMenuVisible(false);
  };

  const handleLogout = () => {
    console.log('üîê Bot√£o de logout clicado');
    console.log('üîê Fun√ß√£o logout dispon√≠vel:', typeof logout);
    console.log('üîê User atual:', user?.email);
    console.log('üîê UserProfile atual:', userProfile?.name);

    if (!logout) {
      console.error('üîê Fun√ß√£o logout n√£o est√° dispon√≠vel!');
      Alert.alert('Erro', 'Fun√ß√£o de logout n√£o est√° dispon√≠vel. Recarregue o app.');
      return;
    }

    // No web, usar modal personalizado; no mobile, usar Alert nativo
    if (Platform.OS === 'web') {
      setLogoutModalVisible(true);
    } else {
      Alert.alert(
        'Confirmar Sa√≠da',
        'Tem certeza que deseja sair da sua conta?',
        [
          {
            text: 'Cancelar',
            style: 'cancel',
            onPress: () => console.log('üîê Logout cancelado pelo usu√°rio')
          },
          {
            text: 'Sair',
            style: 'destructive',
            onPress: async () => {
              try {
                console.log('üîê Iniciando processo de logout...');
                closeMenu(); // Fechar menu antes do logout
                await logout();
                console.log('üîê Logout executado com sucesso');
              } catch (error) {
                console.error('üîê Erro no logout:', error);
                Alert.alert('Erro', 'N√£o foi poss√≠vel fazer logout. Tente novamente.');
              }
            },
          },
        ]
      );
    }
  };

  const confirmLogout = async () => {
    try {
      console.log('üîê Iniciando processo de logout...');
      setLogoutModalVisible(false);
      closeMenu(); // Fechar menu antes do logout
      await logout();
      console.log('üîê Logout executado com sucesso');
    } catch (error) {
      console.error('üîê Erro no logout:', error);
      Alert.alert('Erro', 'N√£o foi poss√≠vel fazer logout. Tente novamente.');
    }
  };

  const cancelLogout = () => {
    console.log('üîê Logout cancelado pelo usu√°rio');
    setLogoutModalVisible(false);
  };

  const handleProfile = () => {
    closeMenu();
    navigation?.navigate('Profile');
  };

  const getUserTypeColor = () => {
    switch (userProfile?.userType) {
      case 'admin':
        return '#FF9800';
      case 'instructor':
        return '#4CAF50';
      case 'student':
        return '#2196F3';
      default:
        return '#4CAF50';
    }
  };

  const getUserTypeLabel = () => {
    switch (userProfile?.userType) {
      case 'admin':
        return 'Administrador';
      case 'instructor':
        return 'Instrutor';
      case 'student':
        return 'Aluno';
      default:
        return 'Usu√°rio';
    }
  };

  console.log('üîê UniversalHeader renderizando - showMenu:', showMenu, 'menuVisible:', menuVisible);

  return (
    <>
      <Appbar.Header style={[styles.header, { backgroundColor: getUserTypeColor() }]}>
        {showBack && (
          <Appbar.BackAction 
            onPress={() => navigation?.goBack()} 
            color="white"
          />
        )}

        <Appbar.Content 
          title="ü•ã Academia App"
          subtitle={subtitle}
          titleStyle={styles.appName}
          subtitleStyle={styles.subtitle}
        />

        {showMenu && <NotificationBell color="white" size={24} />}

        {showMenu && (
          <Menu
            visible={menuVisible}
            onDismiss={closeMenu}
            anchor={
              <TouchableOpacity 
                style={styles.menuAnchor}
                onPress={openMenu}
                activeOpacity={0.7}
              >
                <Avatar.Text 
                  size={ResponsiveUtils?.isTablet?.() ? 40 : 36}
                  label={userProfile?.name?.charAt(0) || 'U'}
                  style={[styles.avatar, { backgroundColor: 'rgba(255,255,255,0.2)' }]}
                  labelStyle={styles.avatarLabel}
                />
              </TouchableOpacity>
            }
            contentStyle={styles.menuContent}
          >
              <View style={styles.menuHeader}>
                <Avatar.Text 
                  size={48}
                  label={userProfile?.name?.charAt(0) || 'U'}
                  style={[styles.menuAvatar, { backgroundColor: getUserTypeColor() }]}
                  labelStyle={styles.menuAvatarLabel}
                />
                <View style={styles.menuUserInfo}>
                  <Menu.Item
                    title={userProfile?.name || 'Usu√°rio'}
                    titleStyle={styles.menuUserName}
                    disabled
                  />
                  <Menu.Item
                    title={getUserTypeLabel()}
                    titleStyle={styles.menuUserType}
                    disabled
                  />
                </View>
              </View>

              <Divider style={styles.menuDivider} />

              <Menu.Item
                onPress={handleProfile}
                title="Meu Perfil"
                leadingIcon={() => (
                  <MaterialCommunityIcons name="account" size={20} color="#666" />
                )}
                titleStyle={styles.menuItemTitle}
              />

              <Menu.Item
                onPress={() => {
                  closeMenu();
                  // Implementar configura√ß√µes se necess√°rio
                }}
                title="Configura√ß√µes"
                leadingIcon={() => (
                  <MaterialCommunityIcons name="cog" size={20} color="#666" />
                )}
                titleStyle={styles.menuItemTitle}
              />

              <Divider style={styles.menuDivider} />

              <Menu.Item
                onPress={() => {
                  closeMenu();
                  handleLogout();
                }}
                title="Sair"
                leadingIcon={() => (
                  <MaterialCommunityIcons name="logout" size={20} color="#F44336" />
                )}
                titleStyle={[styles.menuItemTitle, { color: '#F44336' }]}
              />
            </Menu>
        )}
      </Appbar.Header>

      {/* Modal de confirma√ß√£o de logout para web */}
      <Portal>
        <Modal
          visible={logoutModalVisible}
          onDismiss={cancelLogout}
          contentContainerStyle={styles.modalContainer}
        >
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>Confirmar Sa√≠da</Text>
            <Text style={styles.modalMessage}>
              Tem certeza que deseja sair da sua conta?
            </Text>
            <View style={styles.modalButtons}>
              <Button
                mode="outlined"
                onPress={cancelLogout}
                style={styles.modalButton}
              >
                Cancelar
              </Button>
              <Button
                mode="contained"
                onPress={confirmLogout}
                style={[styles.modalButton, styles.logoutButton]}
                buttonColor="#F44336"
              >
                Sair
              </Button>
            </View>
          </View>
        </Modal>
      </Portal>
    </>
  );
};

const styles = StyleSheet.create({
  header: {
    elevation: 4,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
