import React, { useEffect } from 'react';
import { Platform } from 'react-native';

const WebCompatibility = ({ children }) => {
  useEffect(() => {
    if (Platform.OS === 'web') {
      // Configura√ß√µes espec√≠ficas para web
      console.log('üåê Executando no navegador');

      // Prevenir zoom em inputs no iOS Safari
      const viewport = document.querySelector('meta[name=viewport]');
      if (viewport) {
        viewport.setAttribute('content', 
          'width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=0'
        );
      }

      // Configurar CSS global para web
      const style = document.createElement('style');
      style.textContent = `
        html, body, #root, #app {
          height: 100%;
        }
        body {
          font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
          margin: 0;
          padding: 0;
          overflow-x: hidden;
          overflow-y: auto;
        }

        #root > div {
          min-height: 100vh;
        }

        * {
          box-sizing: border-box;
        }

        input, textarea, select {
          font-family: inherit;
        }

        /* Scrollbar personalizada */
        ::-webkit-scrollbar {
          width: 8px;
        }

        ::-webkit-scrollbar-track {
          background: #f1f1f1;
        }

        ::-webkit-scrollbar-thumb {
          background: #888;
          border-radius: 4px;
        }

        ::-webkit-scrollbar-thumb:hover {
          background: #555;
        }
      `;
      document.head.appendChild(style);
    }
  }, []);

  return children;
};

export default WebCompatibility;

