        {debugInfo ? (
          <Card style={styles.debugCard}>
            <Card.Content>
              <Title>Logs de Debug</Title>
              <Text style={styles.debugText}>
                {debugInfo}
              </Text>
            </Card.Content>
          </Card>
        ) : null}
      </ScrollView>
    </SafeAreaView>
  );
};

