import * as Location from 'expo-location';
import { Alert } from 'react-native';

class LocationService {
  constructor() {
    this.academyLocation = {
      latitude: -23.5505, // Coordenadas da academia (exemplo: S√£o Paulo)
      longitude: -46.6333,
      radius: 100 // Raio em metros para check-in v√°lido
    };
  }

  // Solicitar permiss√£o de localiza√ß√£o
  async requestLocationPermission() {
    try {
      const { status } = await Location.requestForegroundPermissionsAsync();

      if (status !== 'granted') {
        Alert.alert(
          'Permiss√£o Negada',
          '√â necess√°rio permitir acesso √† localiza√ß√£o para fazer check-in nas aulas.'
        );
        return false;
      }

      return true;
    } catch (error) {
      console.error('Erro ao solicitar permiss√£o de localiza√ß√£o:', error);
      return false;
    }
  }

  // Obter localiza√ß√£o atual do usu√°rio
  async getCurrentLocation() {
    try {
      const hasPermission = await this.requestLocationPermission();
      if (!hasPermission) return null;

      const location = await Location.getCurrentPositionAsync({
        accuracy: Location.Accuracy.High,
        timeout: 10000,
        maximumAge: 60000
      });

      return {
        latitude: location.coords.latitude,
        longitude: location.coords.longitude,
        accuracy: location.coords.accuracy,
        timestamp: location.timestamp
      };
    } catch (error) {
      console.error('Erro ao obter localiza√ß√£o:', error);
      Alert.alert(
        'Erro de Localiza√ß√£o',
        'N√£o foi poss√≠vel obter sua localiza√ß√£o. Verifique se o GPS est√° ativado.'
      );
      return null;
    }
  }

  // Calcular dist√¢ncia entre duas coordenadas (f√≥rmula de Haversine)
  calculateDistance(lat1, lon1, lat2, lon2) {
    const R = 6371e3; // Raio da Terra em metros
    const œÜ1 = lat1 * Math.PI / 180;
    const œÜ2 = lat2 * Math.PI / 180;
    const ŒîœÜ = (lat2 - lat1) * Math.PI / 180;
    const ŒîŒª = (lon2 - lon1) * Math.PI / 180;

    const a = Math.sin(ŒîœÜ / 2) * Math.sin(ŒîœÜ / 2) +
              Math.cos(œÜ1) * Math.cos(œÜ2) *
              Math.sin(ŒîŒª / 2) * Math.sin(ŒîŒª / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));

    return R * c; // Dist√¢ncia em metros
  }

  // Verificar se o usu√°rio est√° dentro do raio da academia
  async isWithinAcademyRadius() {
    try {
      const userLocation = await this.getCurrentLocation();
      if (!userLocation) return { valid: false, reason: 'Localiza√ß√£o n√£o dispon√≠vel' };

      const distance = this.calculateDistance(
        userLocation.latitude,
        userLocation.longitude,
        this.academyLocation.latitude,
        this.academyLocation.longitude
      );

      const isValid = distance <= this.academyLocation.radius;

      return {
        valid: isValid,
        distance: Math.round(distance),
        userLocation,
        academyLocation: this.academyLocation,
        reason: isValid ? 'Localiza√ß√£o v√°lida' : `Voc√™ est√° a ${Math.round(distance)}m da academia`
      };
    } catch (error) {
      console.error('Erro ao verificar localiza√ß√£o:', error);
      return { valid: false, reason: 'Erro ao verificar localiza√ß√£o' };
    }
  }

  // Configurar localiza√ß√£o da academia (para admins)
  setAcademyLocation(latitude, longitude, radius = 100) {
    this.academyLocation = {
      latitude,
      longitude,
      radius
    };
  }

  // Obter endere√ßo a partir de coordenadas
  async getAddressFromCoordinates(latitude, longitude) {
    try {
      const addresses = await Location.reverseGeocodeAsync({
        latitude,
        longitude
      });

      if (addresses.length > 0) {
        const address = addresses[0];
        return {
          street: address.street,
          city: address.city,
          region: address.region,
          country: address.country,
          postalCode: address.postalCode,
          formattedAddress: `${address.street}, ${address.city} - ${address.region}`
        };
      }

      return null;
    } catch (error) {
      console.error('Erro ao obter endere√ßo:', error);
      return null;
    }
  }

  // Validar check-in com localiza√ß√£o
  async validateCheckIn(classId) {
    try {
      const locationCheck = await this.isWithinAcademyRadius();

      if (!locationCheck.valid) {
        Alert.alert(
          'Check-in Inv√°lido',
          locationCheck.reason + '\n\nVoc√™ precisa estar na academia para fazer check-in.'
        );
        return {
          success: false,
          reason: locationCheck.reason,
          location: locationCheck.userLocation
        };
      }

      return {
        success: true,
        location: locationCheck.userLocation,
        distance: locationCheck.distance,
        timestamp: new Date().toISOString()
      };
    } catch (error) {
      console.error('Erro na valida√ß√£o do check-in:', error);
      return {
        success: false,
        reason: 'Erro interno na valida√ß√£o',
        location: null
      };
    }
  }
}

export default new LocationService();

