import * as Notifications from 'expo-notifications';
import * as Device from 'expo-device';
import { Platform } from 'react-native';
import { firestoreService } from './firestoreService';

// Configurar comportamento das notifica√ß√µes
Notifications.setNotificationHandler({
  handleNotification: async () => ({
    shouldShowAlert: true,
    shouldPlaySound: true,
    shouldSetBadge: true,
  }),
});

class NotificationService {
  constructor() {
    this.expoPushToken = null;
    this.notificationListener = null;
    this.responseListener = null;
  }

  // Inicializar servi√ßo de notifica√ß√µes
  async initialize() {
    try {
      // Desabilitar notifica√ß√µes push na web por enquanto
      if (Platform.OS === 'web') {
        console.log('Notifica√ß√µes push desabilitadas na web');
        return true;
      }

      await this.registerForPushNotificationsAsync();
      this.setupNotificationListeners();
      return true;
    } catch (error) {
      console.error('Erro ao inicializar notifica√ß√µes:', error);
      return false;
    }
  }

  // Registrar para notifica√ß√µes push
  async registerForPushNotificationsAsync() {
    // Pular registro na web
    if (Platform.OS === 'web') {
      return null;
    }

    let token;

    if (Platform.OS === 'android') {
      await Notifications.setNotificationChannelAsync('default', {
        name: 'default',
        importance: Notifications.AndroidImportance.MAX,
        vibrationPattern: [0, 250, 250, 250],
        lightColor: '#FF231F7C',
      });
    }

    if (Device.isDevice) {
      const { status: existingStatus } = await Notifications.getPermissionsAsync();
      let finalStatus = existingStatus;

      if (existingStatus !== 'granted') {
        const { status } = await Notifications.requestPermissionsAsync();
        finalStatus = status;
      }

      if (finalStatus !== 'granted') {
        console.log('Permiss√£o para notifica√ß√µes negada');
        return;
      }

      token = (await Notifications.getExpoPushTokenAsync()).data;
      this.expoPushToken = token;
    } else {
      console.log('Deve usar um dispositivo f√≠sico para notifica√ß√µes push');
    }

    return token;
  }

  // Configurar listeners de notifica√ß√µes
  setupNotificationListeners() {
    // Listener para notifica√ß√µes recebidas
    this.notificationListener = Notifications.addNotificationReceivedListener(notification => {
      console.log('Notifica√ß√£o recebida:', notification);
      this.handleNotificationReceived(notification);
    });

    // Listener para intera√ß√µes com notifica√ß√µes
    this.responseListener = Notifications.addNotificationResponseReceivedListener(response => {
      console.log('Resposta da notifica√ß√£o:', response);
      this.handleNotificationResponse(response);
    });
  }

  // Manipular notifica√ß√£o recebida
  handleNotificationReceived(notification) {
    const { data } = notification.request.content;

    // Salvar notifica√ß√£o no Firestore
    if (data.userId) {
      this.saveNotificationToFirestore({
        userId: data.userId,
        title: notification.request.content.title,
        message: notification.request.content.body,
        type: data.type || 'general',
        data: data,
        isRead: false,
        createdAt: new Date()
      });
    }
  }

  // Manipular resposta da notifica√ß√£o
  handleNotificationResponse(response) {
    const { data } = response.notification.request.content;

    // Navegar para tela espec√≠fica baseada no tipo
    if (data.screen) {
      // Implementar navega√ß√£o aqui
      console.log('Navegar para:', data.screen);
    }
  }

  // Salvar notifica√ß√£o no Firestore
  async saveNotificationToFirestore(notificationData) {
    try {
      await firestoreService.addDocument('notifications', notificationData);
    } catch (error) {
      console.error('Erro ao salvar notifica√ß√£o:', error);
    }
  }

  // Enviar notifica√ß√£o local
  async sendLocalNotification(title, body, data = {}) {
    try {
      await Notifications.scheduleNotificationAsync({
        content: {
          title,
          body,
          data,
          sound: 'default',
        },
        trigger: null, // Enviar imediatamente
      });
    } catch (error) {
      console.error('Erro ao enviar notifica√ß√£o local:', error);
    }
  }

  // Agendar notifica√ß√£o
  async scheduleNotification(title, body, triggerDate, data = {}) {
    try {
      const trigger = new Date(triggerDate);

      await Notifications.scheduleNotificationAsync({
        content: {
          title,
          body,
          data,
          sound: 'default',
        },
        trigger,
      });
    } catch (error) {
      console.error('Erro ao agendar notifica√ß√£o:', error);
    }
  }

  // Cancelar todas as notifica√ß√µes agendadas
  async cancelAllScheduledNotifications() {
    try {
      await Notifications.cancelAllScheduledNotificationsAsync();
    } catch (error) {
      console.error('Erro ao cancelar notifica√ß√µes:', error);
    }
  }

  // Obter token push
  getExpoPushToken() {
    return this.expoPushToken;
  }

  // Limpar listeners
  cleanup() {
    if (this.notificationListener) {
      Notifications.removeNotificationSubscription(this.notificationListener);
    }
    if (this.responseListener) {
      Notifications.removeNotificationSubscription(this.responseListener);
    }
  }

  // Notifica√ß√µes espec√≠ficas do app
  async notifyPaymentDue(userId, amount, dueDate) {
    const title = 'Pagamento Pendente';
    const body = `Sua mensalidade de R$ ${amount.toFixed(2)} vence em ${new Date(dueDate).toLocaleDateString()}`;

    await this.saveNotificationToFirestore({
      userId,
      title,
      message: body,
      type: 'payment',
      data: { amount, dueDate },
      isRead: false,
      createdAt: new Date()
    });

    await this.sendLocalNotification(title, body, {
      type: 'payment',
      userId,
      screen: 'Pagamentos'
    });
  }

  async notifyClassReminder(userId, className, classTime) {
    const title = 'Lembrete de Aula';
    const body = `Sua aula de ${className} come√ßa em 30 minutos (${classTime})`;

    await this.saveNotificationToFirestore({
      userId,
      title,
      message: body,
      type: 'class',
      data: { className, classTime },
      isRead: false,
      createdAt: new Date()
    });

    await this.sendLocalNotification(title, body, {
      type: 'class',
      userId,
      screen: 'Calend√°rio'
    });
  }

  async notifyGraduation(userId, fromLevel, toLevel, modalityName) {
    const title = 'Parab√©ns! Nova Gradua√ß√£o';
    const body = `Voc√™ foi promovido de ${fromLevel} para ${toLevel} em ${modalityName}!`;

    await this.saveNotificationToFirestore({
      userId,
      title,
      message: body,
      type: 'graduation',
      data: { fromLevel, toLevel, modalityName },
      isRead: false,
      createdAt: new Date()
    });

    await this.sendLocalNotification(title, body, {
      type: 'graduation',
      userId,
      screen: 'Evolu√ß√£o'
    });
  }

  async notifyAnnouncement(userId, announcementTitle, announcementContent) {
    const title = 'Novo An√∫ncio';
    const body = announcementTitle;

    await this.saveNotificationToFirestore({
      userId,
      title,
      message: body,
      type: 'general',
      data: { announcementContent },
      isRead: false,
      createdAt: new Date()
    });

    await this.sendLocalNotification(title, body, {
      type: 'general',
      userId,
      screen: 'Dashboard'
    });
  }
}

export default new NotificationService();

src/services/paymentService.js.content:
import { firestoreService } from './firestoreService';
import notificationService from './notificationService';

class PaymentService {
  constructor() {
    this.paymentProviders = {
      PIX: 'pix',
      CREDIT_CARD: 'credit_card',
      DEBIT_CARD: 'debit_card',
      BANK_SLIP: 'bank_slip',
      CASH: 'cash'
    };

    this.paymentStatus = {
      PENDING: 'pending',
      PAID: 'paid',
      OVERDUE: 'overdue',
      CANCELLED: 'cancelled',
      PROCESSING: 'processing'
    };
  }

  // Criar cobran√ßa PIX
  async createPixPayment(studentId, amount, description, dueDate) {
    try {
      const paymentData = {
        studentId,
        amount,
        description,
        dueDate: new Date(dueDate),
        method: this.paymentProviders.PIX,
        status: this.paymentStatus.PENDING,
        pixData: {
          qrCode: this.generatePixQRCode(amount, description),
          pixKey: 'academia@exemplo.com', // Chave PIX da academia
          expiresAt: new Date(Date.now() + 24 * 60 * 60 * 1000) // 24 horas
        },
        createdAt: new Date(),
        updatedAt: new Date()
      };

      const paymentId = await firestoreService.addDocument('payments', paymentData);

      // Notificar aluno sobre nova cobran√ßa
      await notificationService.notifyPaymentDue(studentId, amount, dueDate);

      return { id: paymentId, ...paymentData };
    } catch (error) {
      console.error('Erro ao criar pagamento PIX:', error);
      throw error;
    }
  }

  // Gerar QR Code PIX (simulado)
  generatePixQRCode(amount, description) {
    // Em produ√ß√£o, usar API do banco para gerar QR Code real
    const pixString = `00020126580014BR.GOV.BCB.PIX0136academia@exemplo.com0208${description}5204000053039865802BR5925ACADEMIA EXEMPLO6009SAO PAULO62070503***6304`;
    return `data:image/png;base64,${Buffer.from(pixString).toString('base64')}`;
  }

  // Processar pagamento com cart√£o (integra√ß√£o simulada)
  async processCardPayment(paymentData, cardData) {
    try {
      // Simular processamento com gateway de pagamento
      const paymentResult = await this.simulateCardProcessing(paymentData, cardData);

      if (paymentResult.success) {
        await this.confirmPayment(paymentData.id, {
          method: paymentData.method,
          transactionId: paymentResult.transactionId,
          authorizationCode: paymentResult.authorizationCode,
          paidAt: new Date()
        });

        return {
          success: true,
          transactionId: paymentResult.transactionId,
          message: 'Pagamento processado com sucesso'
        };
      } else {
        throw new Error(paymentResult.error);
      }
    } catch (error) {
      console.error('Erro no processamento do cart√£o:', error);
      throw error;
    }
  }

  // Simular processamento de cart√£o
  async simulateCardProcessing(paymentData, cardData) {
    // Simular delay de processamento
    await new Promise(resolve => setTimeout(resolve, 2000));

    // Simular valida√ß√µes b√°sicas
    if (!cardData.number || cardData.number.length < 16) {
      return { success: false, error: 'N√∫mero do cart√£o inv√°lido' };
    }

    if (!cardData.cvv || cardData.cvv.length < 3) {
      return { success: false, error: 'CVV inv√°lido' };
    }

    if (!cardData.expiryDate || !this.isValidExpiryDate(cardData.expiryDate)) {
      return { success: false, error: 'Data de validade inv√°lida' };
    }

    // Simular aprova√ß√£o (90% de chance)
    const isApproved = Math.random() > 0.1;

    if (isApproved) {
      return {
        success: true,
        transactionId: `TXN_${Date.now()}`,
        authorizationCode: `AUTH_${Math.random().toString(36).substr(2, 9).toUpperCase()}`
      };
    } else {
      return { success: false, error: 'Pagamento negado pelo banco' };
    }
  }

  // Validar data de validade do cart√£o
  isValidExpiryDate(expiryDate) {
    const [month, year] = expiryDate.split('/');
    const expiry = new Date(2000 + parseInt(year), parseInt(month) - 1);
    return expiry > new Date();
  }

  // Confirmar pagamento
  async confirmPayment(paymentId, confirmationData) {
    try {
      const updateData = {
        status: this.paymentStatus.PAID,
        paidDate: confirmationData.paidAt || new Date(),
        method: confirmationData.method,
        transactionId: confirmationData.transactionId,
        authorizationCode: confirmationData.authorizationCode,
        updatedAt: new Date()
      };

      await firestoreService.updateDocument('payments', paymentId, updateData);

      // Buscar dados do pagamento para notifica√ß√£o
      const payment = await firestoreService.getDocument('payments', paymentId);

      // Notificar confirma√ß√£o de pagamento
      await notificationService.sendLocalNotification(
        'Pagamento Confirmado! ‚úÖ',
        `Seu pagamento de R$ ${payment.amount.toFixed(2)} foi processado com sucesso.`,
        {
          type: 'payment',
          paymentId,
          screen: 'Pagamentos'
        }
      );

      return true;
    } catch (error) {
      console.error('Erro ao confirmar pagamento:', error);
      throw error;
    }
  }

  // Cancelar pagamento
  async cancelPayment(paymentId, reason) {
    try {
      await firestoreService.updateDocument('payments', paymentId, {
        status: this.paymentStatus.CANCELLED,
        cancellationReason: reason,
        cancelledAt: new Date(),
        updatedAt: new Date()
      });

      return true;
    } catch (error) {
      console.error('Erro ao cancelar pagamento:', error);
      throw error;
    }
  }

  // Listar pagamentos do aluno
  async getStudentPayments(studentId, filters = {}) {
    try {
      const queryFilters = [
        { field: 'studentId', operator: '==', value: studentId }
      ];

      if (filters.status) {
        queryFilters.push({ field: 'status', operator: '==', value: filters.status });
      }

      if (filters.startDate) {
        queryFilters.push({ field: 'dueDate', operator: '>=', value: filters.startDate });
      }

      if (filters.endDate) {
        queryFilters.push({ field: 'dueDate', operator: '<=', value: filters.endDate });
      }

      const payments = await firestoreService.getDocumentsWithFilters('payments', queryFilters);

      return payments.sort((a, b) => b.dueDate.toDate() - a.dueDate.toDate());
    } catch (error) {
      console.error('Erro ao buscar pagamentos do aluno:', error);
      throw error;
    }
  }

  // Verificar pagamentos vencidos
  async checkOverduePayments() {
    try {
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      const overduePayments = await firestoreService.getDocumentsWithFilters('payments', [
        { field: 'status', operator: '==', value: this.paymentStatus.PENDING },
        { field: 'dueDate', operator: '<', value: today }
      ]);

      // Atualizar status para vencido
      const updatePromises = overduePayments.map(payment => 
        firestoreService.updateDocument('payments', payment.id, {
          status: this.paymentStatus.OVERDUE,
          updatedAt: new Date()
        })
      );

      await Promise.all(updatePromises);

      // Notificar alunos sobre pagamentos vencidos
      for (const payment of overduePayments) {
        await notificationService.sendLocalNotification(
          'Pagamento Vencido! ‚ö†Ô∏è',
          `Sua mensalidade de R$ ${payment.amount.toFixed(2)} est√° vencida.`,
          {
            type: 'payment',
            paymentId: payment.id,
            screen: 'Pagamentos'
          }
        );
      }

      return overduePayments.length;
    } catch (error) {
      console.error('Erro ao verificar pagamentos vencidos:', error);
      throw error;
    }
  }

  // Gerar relat√≥rio de pagamentos
  async generatePaymentReport(startDate, endDate) {
    try {
      const payments = await firestoreService.getDocumentsWithFilters('payments', [
        { field: 'dueDate', operator: '>=', value: startDate },
        { field: 'dueDate', operator: '<=', value: endDate }
      ]);

      const report = {
        period: { startDate, endDate },
        totalPayments: payments.length,
        totalAmount: 0,
        paidAmount: 0,
        pendingAmount: 0,
        overdueAmount: 0,
        byMethod: {},
        byStatus: {
          paid: 0,
          pending: 0,
          overdue: 0,
          cancelled: 0
        }
      };

      payments.forEach(payment => {
        report.totalAmount += payment.amount;

        // Por status
        report.byStatus[payment.status]++;

        switch (payment.status) {
          case this.paymentStatus.PAID:
            report.paidAmount += payment.amount;
            break;
          case this.paymentStatus.PENDING:
            report.pendingAmount += payment.amount;
            break;
          case this.paymentStatus.OVERDUE:
            report.overdueAmount += payment.amount;
            break;
        }

        // Por m√©todo
        const method = payment.method || 'unknown';
        if (!report.byMethod[method]) {
          report.byMethod[method] = { count: 0, amount: 0 };
        }
        report.byMethod[method].count++;
        report.byMethod[method].amount += payment.amount;
      });

      return report;
    } catch (error) {
      console.error('Erro ao gerar relat√≥rio de pagamentos:', error);
      throw error;
    }
  }

  // Criar mensalidade recorrente
  async createRecurringPayment(studentId, amount, description, startDate, frequency = 'monthly') {
    try {
      const payments = [];
      const currentDate = new Date(startDate);

      // Criar 12 mensalidades
      for (let i = 0; i < 12; i++) {
        const dueDate = new Date(currentDate);

        if (frequency === 'monthly') {
          dueDate.setMonth(currentDate.getMonth() + i);
        }

        const paymentData = {
          studentId,
          amount,
          description: `${description} - ${dueDate.toLocaleDateString('pt-BR', { month: 'long', year: 'numeric' })}`,
          dueDate,
          method: null, // Ser√° definido quando o aluno escolher
          status: this.paymentStatus.PENDING,
          isRecurring: true,
          recurringId: `REC_${Date.now()}_${studentId}`,
          createdAt: new Date(),
          updatedAt: new Date()
        };

        const paymentId = await firestoreService.addDocument('payments', paymentData);
        payments.push({ id: paymentId, ...paymentData });
      }

      return payments;
    } catch (error) {
      console.error('Erro ao criar pagamentos recorrentes:', error);
      throw error;
    }
  }

  // Calcular estat√≠sticas de pagamento
  async getPaymentStats(studentId) {
    try {
      const payments = await this.getStudentPayments(studentId);

      const stats = {
        total: payments.length,
        paid: payments.filter(p => p.status === this.paymentStatus.PAID).length,
        pending: payments.filter(p => p.status === this.paymentStatus.PENDING).length,
        overdue: payments.filter(p => p.status === this.paymentStatus.OVERDUE).length,
        totalAmount: payments.reduce((sum, p) => sum + p.amount, 0),
        paidAmount: payments.filter(p => p.status === this.paymentStatus.PAID).reduce((sum, p) => sum + p.amount, 0),
        pendingAmount: payments.filter(p => p.status === this.paymentStatus.PENDING).reduce((sum, p) => sum + p.amount, 0),
        overdueAmount: payments.filter(p => p.status === this.paymentStatus.OVERDUE).reduce((sum, p) => sum + p.amount, 0),
        paymentRate: 0
      };

      stats.paymentRate = stats.total > 0 ? (stats.paid / stats.total) * 100 : 0;

      return stats;
    } catch (error) {
      console.error('Erro ao calcular estat√≠sticas de pagamento:', error);
      throw error;
    }
  }
}

export default new PaymentService();

