import { Platform, Dimensions } from 'react-native';

// Detectar plataforma
export const isWeb = Platform.OS === 'web';
export const isIOS = Platform.OS === 'ios';
export const isAndroid = Platform.OS === 'android';

// Utilit√°rios de responsividade
export const ResponsiveUtils = {
  // Obter dimens√µes da tela
  getScreenDimensions: () => {
    const { width, height } = Dimensions.get('window');
    return { width, height };
  },

  // Verificar se √© dispositivo m√≥vel (baseado na largura)
  isMobile: () => {
    const { width } = Dimensions.get('window');
    return width < 768;
  },

  // Verificar se √© tablet
  isTablet: () => {
    const { width } = Dimensions.get('window');
    return width >= 768 && width < 1024;
  },

  // Verificar se √© desktop
  isDesktop: () => {
    const { width } = Dimensions.get('window');
    return width >= 1024;
  },

  // Obter breakpoint atual
  getCurrentBreakpoint: () => {
    const { width } = Dimensions.get('window');
    if (width < 768) return 'mobile';
    if (width < 1024) return 'tablet';
    return 'desktop';
  }
};

// Configura√ß√µes espec√≠ficas da plataforma
export const PlatformConfig = {
  web: {
    enableGestures: false,
    headerHeight: 56,
    tabBarHeight: 50,
    statusBarHeight: 0,
  },
  ios: {
    enableGestures: true,
    headerHeight: 88,
    tabBarHeight: 83,
    statusBarHeight: 44,
  },
  android: {
    enableGestures: true,
    headerHeight: 56,
    tabBarHeight: 56,
    statusBarHeight: 24,
  }
};

// Obter configura√ß√£o da plataforma atual
export const getCurrentPlatformConfig = () => {
  return PlatformConfig[Platform.OS] || PlatformConfig.web;
};

// Utilit√°rios para APK/AAB
export const BuildUtils = {
  // Verificar se est√° em modo de produ√ß√£o
  isProduction: () => {
    return !__DEV__;
  },

  // Obter informa√ß√µes da build
  getBuildInfo: () => {
    return {
      platform: Platform.OS,
      version: Platform.Version,
      isProduction: !__DEV__,
      buildType: isAndroid ? 'apk' : 'ipa'
    };
  }
};

export default {
  isWeb,
  isIOS,
  isAndroid,
  ResponsiveUtils,
  PlatformConfig,
  getCurrentPlatformConfig,
  BuildUtils
};

