// Utilit√°rios de valida√ß√£o para formul√°rios
import { useState } from 'react';

export const validators = {
  // Valida√ß√£o de email
  email: (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!email) return 'Email √© obrigat√≥rio';
    if (!emailRegex.test(email)) return 'Email inv√°lido';
    return null;
  },

  // Valida√ß√£o de senha
  password: (password, minLength = 6) => {
    if (!password) return 'Senha √© obrigat√≥ria';
    if (password.length < minLength) return `Senha deve ter pelo menos ${minLength} caracteres`;
    return null;
  },

  // Valida√ß√£o de confirma√ß√£o de senha
  confirmPassword: (password, confirmPassword) => {
    if (!confirmPassword) return 'Confirma√ß√£o de senha √© obrigat√≥ria';
    if (password !== confirmPassword) return 'Senhas n√£o coincidem';
    return null;
  },

  // Valida√ß√£o de nome
  name: (name) => {
    if (!name || !name.trim()) return 'Nome √© obrigat√≥rio';
    if (name.trim().length < 2) return 'Nome deve ter pelo menos 2 caracteres';
    return null;
  },

  // Valida√ß√£o de telefone
  phone: (phone) => {
    if (!phone) return null; // Telefone √© opcional na maioria dos casos
    const phoneRegex = /^\(\d{2}\)\s\d{4,5}-\d{4}$/;
    if (!phoneRegex.test(phone)) return 'Formato: (11) 99999-9999';
    return null;
  },

  // Valida√ß√£o de CPF
  cpf: (cpf) => {
    if (!cpf) return 'CPF √© obrigat√≥rio';

    // Remove caracteres n√£o num√©ricos
    const cleanCpf = cpf.replace(/\D/g, '');

    if (cleanCpf.length !== 11) return 'CPF deve ter 11 d√≠gitos';

    // Verifica se todos os d√≠gitos s√£o iguais
    if (/^(\d)\1{10}$/.test(cleanCpf)) return 'CPF inv√°lido';

    // Valida√ß√£o dos d√≠gitos verificadores
    let sum = 0;
    for (let i = 0; i < 9; i++) {
      sum += parseInt(cleanCpf.charAt(i)) * (10 - i);
    }
    let remainder = (sum * 10) % 11;
    if (remainder === 10 || remainder === 11) remainder = 0;
    if (remainder !== parseInt(cleanCpf.charAt(9))) return 'CPF inv√°lido';

    sum = 0;
    for (let i = 0; i < 10; i++) {
      sum += parseInt(cleanCpf.charAt(i)) * (11 - i);
    }
    remainder = (sum * 10) % 11;
    if (remainder === 10 || remainder === 11) remainder = 0;
    if (remainder !== parseInt(cleanCpf.charAt(10))) return 'CPF inv√°lido';

    return null;
  },

  // Valida√ß√£o de data de nascimento
  birthDate: (date) => {
    if (!date) return 'Data de nascimento √© obrigat√≥ria';

    const birthDate = new Date(date);
    const today = new Date();
    const age = today.getFullYear() - birthDate.getFullYear();

    if (birthDate > today) return 'Data de nascimento n√£o pode ser futura';
    if (age < 5) return 'Idade m√≠nima √© 5 anos';
    if (age > 120) return 'Data de nascimento inv√°lida';

    return null;
  },

  // Valida√ß√£o de valor monet√°rio
  money: (value) => {
    if (!value) return 'Valor √© obrigat√≥rio';
    const numValue = parseFloat(value.toString().replace(',', '.'));
    if (isNaN(numValue) || numValue < 0) return 'Valor inv√°lido';
    return null;
  },

  // Valida√ß√£o de hor√°rio
  time: (hour, minute = 0) => {
    if (hour === null || hour === undefined) return 'Hora √© obrigat√≥ria';
    if (hour < 0 || hour > 23) return 'Hora deve estar entre 0 e 23';
    if (minute < 0 || minute > 59) return 'Minuto deve estar entre 0 e 59';
    return null;
  },

  // Valida√ß√£o de dia da semana
  dayOfWeek: (day) => {
    if (day === null || day === undefined) return 'Dia da semana √© obrigat√≥rio';
    if (day < 0 || day > 6) return 'Dia da semana inv√°lido';
    return null;
  },

  // Valida√ß√£o de texto obrigat√≥rio
  required: (value, fieldName = 'Campo') => {
    if (!value || !value.toString().trim()) return `${fieldName} √© obrigat√≥rio`;
    return null;
  },

  // Valida√ß√£o de sele√ß√£o obrigat√≥ria
  requiredSelect: (value, fieldName = 'Campo') => {
    if (!value || value === '') return `${fieldName} deve ser selecionado`;
    return null;
  }
};

// Formatadores de entrada
export const formatters = {
  // Formatar telefone
  phone: (value) => {
    const numbers = value.replace(/\D/g, '');
    if (numbers.length <= 10) {
      return numbers.replace(/(\d{2})(\d{4})(\d{4})/, '($1) $2-$3');
    }
    return numbers.replace(/(\d{2})(\d{5})(\d{4})/, '($1) $2-$3');
  },

  // Formatar CPF
  cpf: (value) => {
    const numbers = value.replace(/\D/g, '');
    return numbers.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  },

  // Formatar valor monet√°rio
  money: (value) => {
    const numbers = value.replace(/\D/g, '');
    const amount = parseFloat(numbers) / 100;
    return amount.toLocaleString('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    });
  },

  // Formatar apenas n√∫meros
  numbersOnly: (value) => {
    return value.replace(/\D/g, '');
  }
};

// Hook personalizado para valida√ß√£o de formul√°rios
export const useFormValidation = (initialValues, validationRules) => {
  const [values, setValues] = useState(initialValues);
  const [errors, setErrors] = useState({});
  const [touched, setTouched] = useState({});

  const validateField = (name, value) => {
    if (validationRules[name]) {
      const error = validationRules[name](value, values);
      setErrors(prev => ({
        ...prev,
        [name]: error
      }));
      return error;
    }
    return null;
  };

  const validateAll = () => {
    const newErrors = {};
    let isValid = true;

    Object.keys(validationRules).forEach(field => {
      const error = validationRules[field](values[field], values);
      if (error) {
        newErrors[field] = error;
        isValid = false;
      }
    });

    setErrors(newErrors);
    setTouched(Object.keys(validationRules).reduce((acc, key) => {
      acc[key] = true;
      return acc;
    }, {}));

    return isValid;
  };

  const setValue = (name, value) => {
    setValues(prev => ({
      ...prev,
      [name]: value
    }));

    if (touched[name]) {
      validateField(name, value);
    }
  };

  const setTouchedField = (name) => {
    setTouched(prev => ({
      ...prev,
      [name]: true
    }));
    validateField(name, values[name]);
  };

  const reset = () => {
    setValues(initialValues);
    setErrors({});
    setTouched({});
  };

  return {
    values,
    errors,
    touched,
    setValue,
    setTouchedField,
    validateAll,
    reset,
    isValid: Object.keys(errors).length === 0
  };
};

// Valida√ß√µes espec√≠ficas para o dom√≠nio da academia
export const academyValidators = {
  // Valida√ß√£o de gradua√ß√£o
  graduation: (graduation) => {
    const validGraduations = [
      'Iniciante', 'Branca', 'Azul', 'Roxa', 'Marrom', 'Preta',
      'Coral', 'Vermelha', 'Vermelha e Preta'
    ];
    if (!graduation) return 'Gradua√ß√£o √© obrigat√≥ria';
    if (!validGraduations.includes(graduation)) return 'Gradua√ß√£o inv√°lida';
    return null;
  },

  // Valida√ß√£o de modalidade
  modality: (modality) => {
    const validModalities = [
      'Jiu-Jitsu', 'Muay Thai', 'MMA', 'Boxe', 'Wrestling', 'Judo'
    ];
    if (!modality) return 'Modalidade √© obrigat√≥ria';
    if (!validModalities.includes(modality)) return 'Modalidade inv√°lida';
    return null;
  },

  // Valida√ß√£o de plano
  plan: (plan) => {
    if (!plan) return 'Plano √© obrigat√≥rio';
    return null;
  },

  // Valida√ß√£o de capacidade de turma
  classCapacity: (capacity) => {
    const num = parseInt(capacity);
    if (!capacity) return 'Capacidade √© obrigat√≥ria';
    if (isNaN(num) || num < 1) return 'Capacidade deve ser maior que 0';
    if (num > 50) return 'Capacidade m√°xima √© 50 alunos';
    return null;
  }
};

export default {
  validators,
  formatters,
  useFormValidation,
  academyValidators
};

src/screens/admin/AddClassScreen.js.content:
import React, { useState, useEffect } from 'react';
import { 
  View, 
  StyleSheet, 
  ScrollView
} from 'react-native';
import { Card, Text, Button, TextInput, HelperText, Chip, RadioButton, Snackbar } from 'react-native-paper';
import { SafeAreaView } from 'react-native-safe-area-context';
// import * as FileSystem from 'expo-file-system'; // Removido - depend√™ncia n√£o dispon√≠vel
import { useAuth } from '../../contexts/AuthContext';
import { firestoreService, classService } from '../../services/firestoreService';

const AddClassScreen = ({ navigation, route }) => {
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [instructors, setInstructors] = useState([]);
  const [modalities, setModalities] = useState([]);
  const [snackbar, setSnackbar] = useState({ visible: false, message: '', type: 'info' });

  // Age categories for classes
  const ageCategories = [
    { id: 'kids1', label: 'Kids 1 (4-6 anos)', value: 'kids1', minAge: 4, maxAge: 6 },
    { id: 'kids2', label: 'Kids 2 (7-9 anos)', value: 'kids2', minAge: 7, maxAge: 9 },
    { id: 'kids3', label: 'Kids 3 (10-13 anos)', value: 'kids3', minAge: 10, maxAge: 13 },
    { id: 'juvenil', label: 'Juvenil (14-17 anos)', value: 'juvenil', minAge: 14, maxAge: 17 },
    { id: 'adulto', label: 'Adulto (18+ anos)', value: 'adulto', minAge: 18, maxAge: null }
  ];

  // Form data
  const [formData, setFormData] = useState({
    name: '',
    modality: '',
    description: '',
    maxStudents: '',
    instructorId: '',
    instructorName: '',
    schedule: '',
    price: '',
    status: 'active',
    ageCategory: ''
  });

  const [errors, setErrors] = useState({});

  // Utilit√°rio: converte texto de hor√°rio em array estruturado [{ dayOfWeek, hour, minute }]
  const parseScheduleTextToArray = (text) => {
    if (!text || typeof text !== 'string') return [];
    const dayMap = {
      'domingo': 0, 'dom': 0,
      'segunda': 1, 'segunda-feira': 1, 'seg': 1,
      'terca': 2, 'ter√ßa': 2, 'ter√ßa-feira': 2, 'ter': 2,
      'quarta': 3, 'quarta-feira': 3, 'qua': 3,
      'quinta': 4, 'quinta-feira': 4, 'qui': 4,
      'sexta': 5, 'sexta-feira': 5, 'sex': 5,
      'sabado': 6, 's√°bado': 6, 'sab': 6, 's√°b': 6
    };
    // Suporta m√∫ltiplos hor√°rios separados por "," ou "\n"
    const parts = text.split(/[\,\n]+/).map(p => p.trim()).filter(Boolean);
    const items = [];
    for (const part of parts) {
      // Ex: "Seg 08:00-09:00" ou "Segunda-feira 08:00"
      const m = part.match(/^(\D+?)\s+(\d{1,2}):(\d{2})/i);
      if (!m) continue;
      const dayRaw = m[1].trim().toLowerCase();
      const hour = parseInt(m[2], 10);
      const minute = parseInt(m[3], 10) || 0;
      const dayOfWeek = dayMap[dayRaw];
      if (typeof dayOfWeek === 'number' && !isNaN(hour)) {
        items.push({ dayOfWeek, hour, minute });
      }
    }
    return items;
  };

  useEffect(() => {
    loadInstructors();
    loadModalities();
  }, []);

  const loadInstructors = async () => {
    try {
      const users = await firestoreService.getAll('users');
      const instructorsList = users.filter(user => user.userType === 'instructor');
      setInstructors(instructorsList);
    } catch (error) {
      console.error('Erro ao carregar instrutores:', error);
    }
  };

  const loadModalities = async () => {
    try {
      const list = await firestoreService.getAll('modalities');
      // Normalizar: garantir pelo menos name
      const normalized = (list || []).map((m) => ({ id: m.id || m.name, name: m.name }));
      setModalities(normalized);
    } catch (error) {
      console.error('Erro ao carregar modalidades:', error);
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Nome da turma √© obrigat√≥rio';
    }

    // Bloquear quando n√£o houver modalidades cadastradas no sistema
    if (!modalities || modalities.length === 0) {
      newErrors.modality = 'Nenhuma modalidade cadastrada. V√° em Admin > Modalidades para cadastrar antes de continuar.';
    }

    if (!formData.modality) {
      newErrors.modality = 'Modalidade √© obrigat√≥ria';
    }

    if (!formData.maxStudents || isNaN(formData.maxStudents) || parseInt(formData.maxStudents) <= 0) {
      newErrors.maxStudents = 'N√∫mero m√°ximo de alunos deve ser um n√∫mero positivo';
    }

    if (!formData.instructorId) {
      newErrors.instructorId = 'Instrutor √© obrigat√≥rio';
    }

    if (!formData.schedule.trim()) {
      newErrors.schedule = 'Hor√°rio √© obrigat√≥rio';
    }

    if (!formData.price || isNaN(formData.price) || parseFloat(formData.price) < 0) {
      newErrors.price = 'Pre√ßo deve ser um n√∫mero v√°lido';
    }

    if (!formData.ageCategory) {
      newErrors.ageCategory = 'Categoria de idade √© obrigat√≥ria';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async () => {
    if (!validateForm()) {
      return;
    }

    try {
      setLoading(true);

      const classData = {
        name: formData.name.trim(),
        modality: formData.modality,
        description: formData.description.trim(),
        maxStudents: parseInt(formData.maxStudents),
        currentStudents: 0,
        instructorId: formData.instructorId,
        instructorName: formData.instructorName,
        // Armazenar formato estruturado e manter texto para compatibilidade
        schedule: parseScheduleTextToArray(formData.schedule.trim()),
        scheduleText: formData.schedule.trim(),
        price: parseFloat(formData.price),
        status: formData.status,
        ageCategory: formData.ageCategory,
        createdBy: user.uid,
        createdAt: new Date(),
        updatedAt: new Date()
      };

      await firestoreService.create('classes', classData);
      setSnackbar({ visible: true, message: 'Turma criada com sucesso!', type: 'success' });
      // Voltar ap√≥s pequeno atraso para permitir ver o feedback
      setTimeout(() => navigation.goBack(), 800);

    } catch (error) {
      console.error('Erro ao criar turma:', error);
      setSnackbar({ visible: true, message: 'Erro ao criar turma. Tente novamente.', type: 'error' });
    } finally {
      setLoading(false);
    }
  };

  const updateFormData = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));

    // Clear error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({
        ...prev,
        [field]: null
      }));
    }
  };

  const handleInstructorChange = (instructorId) => {
    const instructor = instructors.find(i => i.id === instructorId);
    updateFormData('instructorId', instructorId);
    updateFormData('instructorName', instructor ? instructor.name : '');
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView 
        style={styles.scrollView}
        showsVerticalScrollIndicator={false}
        keyboardShouldPersistTaps="handled"
        contentContainerStyle={styles.scrollContent}
      >
        <Card style={styles.card}>
          <Card.Content>
            <Text style={styles.title}>Nova Turma</Text>

            {/* Nome da Turma */}
            <TextInput
              label="Nome da Turma"
              value={formData.name}
              onChangeText={(value) => updateFormData('name', value)}
              mode="outlined"
              style={styles.input}
              error={!!errors.name}
            />
            {errors.name && <HelperText type="error">{errors.name}</HelperText>}

            {/* Modalidade */}
            <View style={styles.pickerContainer}>
              <Text style={styles.label}>Modalidade</Text>
              <View style={styles.chipContainer}>
                {modalities.length === 0 && (
                  <Text style={{ color: '#666' }}>Nenhuma modalidade cadastrada</Text>
                )}
                {modalities.map((m) => (
                  <Chip
                    key={m.id}
                    selected={formData.modality === m.name}
                    onPress={() => updateFormData('modality', m.name)}
                    style={styles.chip}
                    mode={formData.modality === m.name ? 'flat' : 'outlined'}
                  >
                    {m.name}
                  </Chip>
                ))}
              </View>
              {errors.modality && <HelperText type="error">{errors.modality}</HelperText>}
            </View>

            {/* Categoria por Idade */}
            <View style={styles.pickerContainer}>
              <Text style={styles.label}>Categoria por Idade</Text>
              <View style={styles.chipContainer}>
                {ageCategories.map((category) => (
                  <Chip
                    key={category.id}
                    selected={formData.ageCategory === category.value}
                    onPress={() => updateFormData('ageCategory', category.value)}
                    style={styles.chip}
                    mode={formData.ageCategory === category.value ? 'flat' : 'outlined'}
                  >
                    {category.label}
                  </Chip>
                ))}
              </View>
              {errors.ageCategory && <HelperText type="error">{errors.ageCategory}</HelperText>}
            </View>

            {/* Descri√ß√£o */}
            <TextInput
              label="Descri√ß√£o (opcional)"
              value={formData.description}
              onChangeText={(value) => updateFormData('description', value)}
              mode="outlined"
              multiline
              numberOfLines={3}
              style={styles.input}
            />

            {/* M√°ximo de Alunos */}
            <TextInput
              label="M√°ximo de Alunos"
              value={formData.maxStudents}
              onChangeText={(value) => updateFormData('maxStudents', value)}
              mode="outlined"
              keyboardType="numeric"
              style={styles.input}
              error={!!errors.maxStudents}
            />
            {errors.maxStudents && <HelperText type="error">{errors.maxStudents}</HelperText>}

            {/* Instrutor */}
            <View style={styles.pickerContainer}>
              <Text style={styles.label}>Instrutor</Text>
              <View style={styles.chipContainer}>
                {instructors.length === 0 && (
                  <Text style={{ color: '#666' }}>Nenhum instrutor encontrado</Text>
                )}
                {instructors.map((instructor) => (
                  <Chip
                    key={instructor.id}
                    selected={formData.instructorId === instructor.id}
                    onPress={() => handleInstructorChange(instructor.id)}
                    style={styles.chip}
                    mode={formData.instructorId === instructor.id ? 'flat' : 'outlined'}
                  >
                    {instructor.name}
                  </Chip>
                ))}
              </View>
              {errors.instructorId && <HelperText type="error">{errors.instructorId}</HelperText>}
            </View>

            {/* Hor√°rio */}
            <TextInput
              label="Hor√°rio (ex: Segunda-feira 08:00-09:00)"
              value={formData.schedule}
              onChangeText={(value) => updateFormData('schedule', value)}
              mode="outlined"
              style={styles.input}
              error={!!errors.schedule}
            />
            {errors.schedule && <HelperText type="error">{errors.schedule}</HelperText>}
            {!errors.schedule && (
              <Text style={styles.helperTip}>
                Dica: voc√™ pode informar v√°rios hor√°rios separados por v√≠rgula. Exemplos: "Seg 08:00, Qua 19:30" ou "Ter√ßa-feira 07:15".
              </Text>
            )}

            {/* Pre√ßo */}
            <TextInput
              label="Pre√ßo Mensal (R$)"
              value={formData.price}
              onChangeText={(value) => updateFormData('price', value)}
              mode="outlined"
              keyboardType="numeric"
              style={styles.input}
              error={!!errors.price}
            />
            {errors.price && <HelperText type="error">{errors.price}</HelperText>}

            {/* Status */}
            <View style={styles.radioContainer}>
              <Text style={styles.label}>Status</Text>
              <RadioButton.Group
                onValueChange={(value) => updateFormData('status', value)}
                value={formData.status}
              >
                <View style={styles.radioItem}>
                  <RadioButton value="active" />
                  <Text style={styles.radioLabel}>Ativa</Text>
                </View>
                <View style={styles.radioItem}>
                  <RadioButton value="inactive" />
                  <Text style={styles.radioLabel}>Inativa</Text>
                </View>
              </RadioButton.Group>
            </View>

            {/* Bot√µes */}
            <View style={styles.buttonContainer}>
              <Button
                mode="outlined"
                onPress={() => navigation.goBack()}
                style={styles.button}
                disabled={loading}
              >
                Cancelar
              </Button>
              <Button
                mode="contained"
                onPress={handleSubmit}
                style={styles.button}
                loading={loading}
                disabled={loading || modalities.length === 0}
              >
                Criar Turma
              </Button>
            </View>
          </Card.Content>
        </Card>
      </ScrollView>
      <Snackbar
        visible={snackbar.visible}
        onDismiss={() => setSnackbar((s) => ({ ...s, visible: false }))}
        duration={2500}
      >
        {snackbar.message}
      </Snackbar>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
  },
  scrollView: {
    flex: 1,
  },
  scrollContent: {
    padding: 16,
    paddingBottom: 100,
  },
  card: {
    marginBottom: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    textAlign: 'center',
  },
  input: {
    marginBottom: 12,
  },
  label: {
    fontSize: 16,
    fontWeight: '500',
    marginBottom: 8,
    color: '#333',
  },
  pickerContainer: {
    marginBottom: 16,
  },
  chipContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    gap: 8,
  },
  chip: {
    marginBottom: 8,
  },
  picker: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 4,
    backgroundColor: '#fff',
  },
  pickerStyle: {
    height: 50,
  },
  radioContainer: {
    marginBottom: 20,
  },
  radioItem: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 8,
  },
  radioLabel: {
    marginLeft: 8,
    fontSize: 16,
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 20,
  },
  button: {
    flex: 1,
    marginHorizontal: 8,
  },
  helperTip: {
    marginTop: -4,
    marginBottom: 12,
