src/screens/auth/ForgotPasswordScreen.js.content:
import React, { useState } from 'react';
import { View, StyleSheet, Alert, ScrollView, KeyboardAvoidingView, Platform, Dimensions } from 'react-native';
import { 
  TextInput, 
  Card, 
  Title, 
  Paragraph,
  Button,
  ActivityIndicator,
} from 'react-native-paper';
import { SafeAreaView } from 'react-native-safe-area-context';
import { LinearGradient } from 'expo-linear-gradient';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { sendPasswordResetEmail } from 'firebase/auth';
import { auth } from '../../services/firebase';
import AnimatedCard from '../../components/AnimatedCard';
import AnimatedButton from '../../components/AnimatedButton';
import { ResponsiveUtils } from '../../utils/animations';

export default function ForgotPasswordScreen({ navigation }) {
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [emailSent, setEmailSent] = useState(false);

  const handleResetPassword = async () => {
    if (!email.trim()) {
      Alert.alert('Erro', 'Por favor, digite seu email');
      return;
    }

    if (!/\S+@\S+\.\S+/.test(email)) {
      Alert.alert('Erro', 'Por favor, digite um email v√°lido');
      return;
    }

    setLoading(true);
    try {
      await sendPasswordResetEmail(auth, email.trim());
      setEmailSent(true);
      Alert.alert(
        'Email Enviado!',
        'Verifique sua caixa de entrada e siga as instru√ß√µes para redefinir sua senha.',
        [
          {
            text: 'OK',
            onPress: () => navigation.goBack()
          }
        ]
      );
    } catch (error) {
      console.error('Erro ao enviar email:', error);
      let errorMessage = 'Erro ao enviar email de recupera√ß√£o';

      if (error.code === 'auth/user-not-found') {
        errorMessage = 'Email n√£o encontrado';
      } else if (error.code === 'auth/invalid-email') {
        errorMessage = 'Email inv√°lido';
      }

      Alert.alert('Erro', errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <LinearGradient
      colors={['#667eea', '#764ba2']}
      style={styles.gradient}
    >
      <SafeAreaView style={styles.container}>
        <KeyboardAvoidingView 
          style={styles.keyboardAvoidingView}
          behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
        >
          <ScrollView 
            style={styles.scrollView}
            contentContainerStyle={styles.scrollContent}
            showsVerticalScrollIndicator={false}
            keyboardShouldPersistTaps="handled"
          >
            <View style={styles.header}>
              <MaterialCommunityIcons 
                name="lock-reset" 
                size={ResponsiveUtils?.isTablet?.() ? 80 : 60} 
                color="white" 
                style={styles.headerIcon}
              />
              <Title style={styles.headerTitle}>Recuperar Senha</Title>
              <Paragraph style={styles.headerSubtitle}>
                Digite seu email para receber instru√ß√µes
              </Paragraph>
            </View>

            <View style={styles.content}>
              <AnimatedCard elevation="medium" animationType="fadeIn">
                <Card.Content style={styles.cardContent}>
              {!emailSent ? (
                <>
                  <TextInput
                    label="Email"
                    value={email}
                    onChangeText={setEmail}
                    mode="outlined"
                    style={styles.input}
                    keyboardType="email-address"
                    autoCapitalize="none"
                    disabled={loading}
                    left={<TextInput.Icon icon="email" />}
                  />

                  <AnimatedButton
                    mode="contained"
                    onPress={handleResetPassword}
                    style={styles.resetButton}
                    loading={loading}
                    disabled={loading}
                    icon="email-send"
                  >
                    Enviar Email
                  </AnimatedButton>
                </>
              ) : (
                <View style={styles.successContainer}>
                  <MaterialCommunityIcons 
                    name="email-check" 
                    size={48} 
                    color="#4CAF50" 
                    style={styles.successIcon}
                  />
                  <Title style={styles.successTitle}>Email Enviado!</Title>
                  <Paragraph style={styles.successText}>
                    Verifique sua caixa de entrada e siga as instru√ß√µes para redefinir sua senha.
                  </Paragraph>
                </View>
              )}

              <View style={styles.backContainer}>
                <Button
                  mode="text"
                  onPress={() => navigation.goBack()}
                  disabled={loading}
                  icon="arrow-left"
                >
                  Voltar ao Login
                </Button>
              </View>
            </Card.Content>
          </AnimatedCard>
        </View>
          </ScrollView>
        </KeyboardAvoidingView>
      </SafeAreaView>
    </LinearGradient>
  );
}

const styles = StyleSheet.create({
  gradient: {
    flex: 1,
  },
  container: {
    flex: 1,
    backgroundColor: 'transparent',
  },
  keyboardAvoidingView: {
    flex: 1,
  },
  scrollView: {
    flex: 1,
  },
  scrollContent: {
    flexGrow: 1,
    paddingBottom: ResponsiveUtils?.spacing?.xl || 32,
  },
  header: {
    alignItems: 'center',
    padding: ResponsiveUtils?.spacing?.xl || 32,
    paddingBottom: ResponsiveUtils?.spacing?.md || 16,
  },
  headerIcon: {
    marginBottom: 16,
  },
  headerTitle: {
    fontSize: ResponsiveUtils?.fontSize?.xlarge || 28,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: ResponsiveUtils?.spacing?.sm || 8,
    textAlign: 'center',
  },
  headerSubtitle: {
    fontSize: ResponsiveUtils?.fontSize?.medium || 16,
    color: 'rgba(255, 255, 255, 0.9)',
    textAlign: 'center',
  },
  content: {
    flex: 1,
    paddingHorizontal: ResponsiveUtils?.spacing?.md || 16,
    maxWidth: ResponsiveUtils?.isTablet?.() ? 500 : 400,
    alignSelf: 'center',
    width: '100%',
  },
  cardContent: {
    padding: ResponsiveUtils?.spacing?.lg || 24,
  },
  input: {
    marginBottom: ResponsiveUtils?.spacing?.md || 16,
    backgroundColor: 'white',
  },
  resetButton: {
    marginTop: ResponsiveUtils?.spacing?.sm || 8,
    paddingVertical: ResponsiveUtils?.spacing?.sm || 8,
    borderRadius: ResponsiveUtils?.borderRadius?.large || 25,
  },
  successContainer: {
    alignItems: 'center',
    paddingVertical: ResponsiveUtils?.spacing?.md || 16,
  },
  successIcon: {
    marginBottom: ResponsiveUtils?.spacing?.md || 16,
  },
  successTitle: {
    color: '#4CAF50',
    marginBottom: ResponsiveUtils?.spacing?.sm || 8,
    textAlign: 'center',
  },
  successText: {
    textAlign: 'center',
    color: '#666',
  },
  backContainer: {
    alignItems: 'center',
    marginTop: ResponsiveUtils?.spacing?.md || 16,
  },
});

src/screens/auth/RegisterScreen.js.content:
import React, { useState, useEffect, useRef } from 'react';
import { View, StyleSheet, ScrollView, Animated, Alert, KeyboardAvoidingView, Platform, Dimensions } from 'react-native';
import { 
  TextInput, 
  Button, 
  Text, 
  Card, 
  Title, 
  Paragraph,
  Divider,
  ActivityIndicator,
  RadioButton,
  Chip,
  Snackbar,
  HelperText
} from 'react-native-paper';
import { SafeAreaView } from 'react-native-safe-area-context';
import { LinearGradient } from 'expo-linear-gradient';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { useAuth } from '../../contexts/AuthContext';

const { width } = Dimensions.get('window');

const RegisterScreen = ({ navigation }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    phone: '',
    userType: 'student'
  });
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [errors, setErrors] = useState({});
  const [snackbar, setSnackbar] = useState({ visible: false, message: '', type: 'error' });

  // Anima√ß√µes
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(50)).current;
  const scaleAnim = useRef(new Animated.Value(0.95)).current;

  const { signUp } = useAuth();

  useEffect(() => {
    // Anima√ß√£o de entrada
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 800,
        useNativeDriver: Platform.OS !== 'web',
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 800,
        useNativeDriver: Platform.OS !== 'web',
      }),
      Animated.timing(scaleAnim, {
        toValue: 1,
        duration: 800,
        useNativeDriver: Platform.OS !== 'web',
      }),
    ]).start();
  }, []);

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Nome √© obrigat√≥rio';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email √© obrigat√≥rio';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email inv√°lido';
    }

    if (formData.password.length < 6) {
      newErrors.password = 'Senha deve ter pelo menos 6 caracteres';
    }

    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Senhas n√£o coincidem';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const showSnackbar = (message, type = 'error') => {
    setSnackbar({ visible: true, message, type });
  };

  const handleRegister = async () => {
    if (!validateForm()) return;

    setLoading(true);
    try {
      const userData = {
        name: formData.name.trim(),
        phone: formData.phone.trim(),
        userType: formData.userType,
        isActive: true,
        currentGraduation: formData.userType === 'student' ? 'Iniciante' : null,
        graduations: [],
        classIds: []
      };

      await signUp(formData.email, formData.password, userData);
      showSnackbar('Conta criada com sucesso! üéâ', 'success');

      // Anima√ß√£o de sucesso
      Animated.sequence([
        Animated.timing(scaleAnim, {
          toValue: 1.05,
          duration: 200,
          useNativeDriver: Platform.OS !== 'web',
        }),
        Animated.timing(scaleAnim, {
          toValue: 1,
          duration: 200,
          useNativeDriver: Platform.OS !== 'web',
        }),
      ]).start();
    } catch (error) {
      console.error('Erro no cadastro:', error);
      let errorMessage = 'Erro ao criar conta';

      if (error.code === 'auth/email-already-in-use') {
        errorMessage = 'Este email j√° est√° em uso';
      } else if (error.code === 'auth/weak-password') {
        errorMessage = 'Senha muito fraca';
      } else if (error.code === 'auth/invalid-email') {
        errorMessage = 'Email inv√°lido';
      }

      showSnackbar(errorMessage, 'error');

      // Anima√ß√£o de erro (shake)
      Animated.sequence([
        Animated.timing(slideAnim, { toValue: -10, duration: 100, useNativeDriver: Platform.OS !== 'web' }),
        Animated.timing(slideAnim, { toValue: 10, duration: 100, useNativeDriver: Platform.OS !== 'web' }),
        Animated.timing(slideAnim, { toValue: -5, duration: 100, useNativeDriver: Platform.OS !== 'web' }),
        Animated.timing(slideAnim, { toValue: 0, duration: 100, useNativeDriver: Platform.OS !== 'web' }),
      ]).start();
    } finally {
      setLoading(false);
    }
  };

  const updateFormData = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    // Limpar erro do campo quando usu√°rio come√ßar a digitar
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: null }));
    }
  };

  const getUserTypeIcon = (type) => {
    switch (type) {
      case 'student': return 'school';
      case 'instructor': return 'account-tie';
      case 'admin': return 'shield-account';
      default: return 'account';
    }
  };

  const getUserTypeColor = (type) => {
    switch (type) {
      case 'student': return '#4CAF50';
      case 'instructor': return '#FF9800';
      case 'admin': return '#F44336';
      default: return '#2196F3';
    }
  };

  return (
    <LinearGradient
      colors={['#667eea', '#764ba2']}
      style={styles.gradient}
    >
      <SafeAreaView style={styles.container}>
        <ScrollView
          horizontal={false}
          style={styles.scroll}
          contentContainerStyle={styles.scrollContainer}
          keyboardShouldPersistTaps="handled"
          showsVerticalScrollIndicator={true}
        >
          <Animated.View 
            style={[
              styles.header,
              {
                opacity: fadeAnim,
                transform: [{ translateY: slideAnim }]
              }
            ]}
          >
            <MaterialCommunityIcons 
              name="account-plus" 
              size={60} 
              color="white" 
              style={styles.headerIcon}
            />
            <Title style={styles.title}>Criar Conta</Title>
            <Paragraph style={styles.subtitle}>
              Preencha os dados para se cadastrar
            </Paragraph>
          </Animated.View>

          <Animated.View
            style={[
              { transform: [{ scale: scaleAnim }, { translateX: slideAnim }] }
            ]}
          >
            <Card style={styles.card}>
          <Card.Content>
            <Title style={styles.cardTitle}>Dados Pessoais</Title>

            <TextInput
              label="Nome Completo *"
              value={formData.name}
              onChangeText={(text) => updateFormData('name', text)}
              mode="outlined"
              style={styles.input}
              disabled={loading}
              error={!!errors.name}
              left={<TextInput.Icon icon="account" />}
            />
            {errors.name && (
              <HelperText type="error" visible={!!errors.name}>
                {errors.name}
              </HelperText>
            )}

            <TextInput
              label="Email *"
              value={formData.email}
              onChangeText={(text) => updateFormData('email', text)}
              mode="outlined"
              keyboardType="email-address"
              autoCapitalize="none"
              style={styles.input}
              disabled={loading}
              error={!!errors.email}
              left={<TextInput.Icon icon="email" />}
            />
            {errors.email && (
              <HelperText type="error" visible={!!errors.email}>
                {errors.email}
              </HelperText>
            )}

            <TextInput
              label="Telefone/WhatsApp"
              value={formData.phone}
              onChangeText={(text) => updateFormData('phone', text)}
              mode="outlined"
              keyboardType="phone-pad"
              style={styles.input}
              disabled={loading}
              left={<TextInput.Icon icon="phone" />}
            />

            <Divider style={styles.divider} />

            <Title style={styles.sectionTitle}>Tipo de Usu√°rio</Title>
            <View style={styles.userTypeContainer}>
              {[
                { value: 'student', label: 'Aluno', description: 'Acesso √†s aulas e evolu√ß√£o' },
                { value: 'instructor', label: 'Professor', description: 'Gerenciar turmas e alunos' },
                { value: 'admin', label: 'Administrador', description: 'Controle total do sistema' }
              ].map((type) => (
                <Card 
                  key={type.value}
                  style={[
                    styles.userTypeCard,
                    formData.userType === type.value && {
                      borderColor: getUserTypeColor(type.value),
                      borderWidth: 2,
                      backgroundColor: getUserTypeColor(type.value) + '10'
                    }
                  ]}
                  onPress={() => updateFormData('userType', type.value)}
                >
                  <Card.Content style={styles.userTypeCardContent}>
                    <View style={styles.userTypeInfo}>
                      <MaterialCommunityIcons 
                        name={getUserTypeIcon(type.value)} 
                        size={24} 
                        color={getUserTypeColor(type.value)}
                      />
                      <View style={styles.userTypeText}>
                        <Text style={styles.userTypeLabel}>{type.label}</Text>
                        <Text style={styles.userTypeDescription}>{type.description}</Text>
                      </View>
                    </View>
                    <RadioButton
                      value={type.value}
                      status={formData.userType === type.value ? 'checked' : 'unchecked'}
                      onPress={() => updateFormData('userType', type.value)}
                      disabled={loading}
                    />
                  </Card.Content>
                </Card>
              ))}
            </View>

            <Divider style={styles.divider} />

            <Title style={styles.sectionTitle}>Senha</Title>

            <TextInput
              label="Senha *"
              value={formData.password}
              onChangeText={(text) => updateFormData('password', text)}
              mode="outlined"
              secureTextEntry={!showPassword}
              left={<TextInput.Icon icon="lock" />}
              right={
                <TextInput.Icon 
                  icon={showPassword ? "eye-off" : "eye"} 
                  onPress={() => setShowPassword(!showPassword)}
                />
              }
              style={styles.input}
              disabled={loading}
              error={!!errors.password}
            />
            {errors.password && (
              <HelperText type="error" visible={!!errors.password}>
                {errors.password}
              </HelperText>
            )}

            <TextInput
              label="Confirmar Senha *"
              value={formData.confirmPassword}
              onChangeText={(text) => updateFormData('confirmPassword', text)}
              mode="outlined"
              secureTextEntry={!showConfirmPassword}
              left={<TextInput.Icon icon="lock-check" />}
              right={
                <TextInput.Icon 
                  icon={showConfirmPassword ? "eye-off" : "eye"} 
                  onPress={() => setShowConfirmPassword(!showConfirmPassword)}
                />
              }
              style={styles.input}
              disabled={loading}
              error={!!errors.confirmPassword}
            />
            {errors.confirmPassword && (
              <HelperText type="error" visible={!!errors.confirmPassword}>
                {errors.confirmPassword}
              </HelperText>
            )}

            <Text style={styles.passwordHint}>
              * A senha deve ter pelo menos 6 caracteres
            </Text>

            <Button
              mode="contained"
              onPress={handleRegister}
              style={styles.button}
              disabled={loading}
              icon={loading ? undefined : "account-plus"}
              contentStyle={styles.buttonContent}
            >
              {loading ? (
                <View style={styles.loadingContainer}>
                  <ActivityIndicator color="white" size="small" />
                  <Text style={styles.loadingText}>Criando conta...</Text>
                </View>
              ) : (
                'Criar Conta'
              )}
            </Button>

            <View style={styles.loginContainer}>
              <Text>J√° tem uma conta? </Text>
              <Button
                mode="text"
                onPress={() => navigation.navigate('Login')}
                disabled={loading}
              >
                Fazer Login
              </Button>
            </View>
          </Card.Content>
            </Card>
          </Animated.View>
        </ScrollView>

        <Snackbar
          visible={snackbar.visible}
          onDismiss={() => setSnackbar({ ...snackbar, visible: false })}
          duration={4000}
          style={[
            styles.snackbar,
            snackbar.type === 'success' ? styles.successSnackbar : styles.errorSnackbar
          ]}
          action={{
            label: 'OK',
            onPress: () => setSnackbar({ ...snackbar, visible: false }),
          }}
        >
          {snackbar.message}
        </Snackbar>
      </SafeAreaView>
    </LinearGradient>
  );
};

