      horario: '19:00',
      alunos: 12,
      capacidade: 20,
      status: 'ativo'
    },
    {
      id: 2,
      aula: 'Muay Thai Avan√ßado',
      horario: '20:30',
      alunos: 8,
      capacidade: 15,
      status: 'aguardando'
    }
  ]);

  const [recentCheckIns, setRecentCheckIns] = useState([
    { id: 1, aluno: 'Jo√£o Silva', aula: 'Karat√™', horario: '18:45' },
    { id: 2, aluno: 'Maria Santos', aula: 'Karat√™', horario: '18:47' },
    { id: 3, aluno: 'Pedro Costa', aula: 'Karat√™', horario: '18:50' }
  ]);

  const handleStartCheckIn = (aulaId) => {
    console.log('Iniciando check-in para aula:', aulaId);
  };

  const handleStopCheckIn = (aulaId) => {
    console.log('Parando check-in para aula:', aulaId);
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView style={styles.scrollView}>
        {/* Check-ins Ativos */}
        <Card style={styles.card}>
          <Card.Content>
            <View style={styles.header}>
              <MaterialCommunityIcons name="qrcode-scan" size={32} color="#2196F3" />
              <Title style={styles.title}>Check-ins Ativos</Title>
