import React, { useState } from 'react';
import { View, StyleSheet, ScrollView } from 'react-native';
import { 
  Card, 
  Title, 
  TextInput, 
  Button, 
  Chip, 
  Text,
  Surface,
  Divider 
} from 'react-native-paper';
import { SafeAreaView } from 'react-native-safe-area-context';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { ResponsiveUtils } from '../../utils/animations';

const NovaAula = ({ navigation }) => {
  const [formData, setFormData] = useState({
    nome: '',
    modalidade: '',
    capacidade: '',
    horario: '',
    descricao: ''
  });

  const modalidades = ['Karat√™', 'Jiu-Jitsu', 'Muay Thai', 'Boxe', 'Taekwondo', 'Judo'];

  const handleSubmit = () => {
    // Implementar l√≥gica de cria√ß√£o da aula
    console.log('Nova aula:', formData);
    navigation.goBack();
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView style={styles.scrollView}>
        <Card style={styles.card}>
          <Card.Content>
            <View style={styles.header}>
              <MaterialCommunityIcons name="plus-circle" size={32} color="#4CAF50" />
              <Title style={styles.title}>Nova Aula</Title>
            </View>

            <TextInput
              label="Nome da Aula"
              value={formData.nome}
              onChangeText={(text) => setFormData({...formData, nome: text})}
              style={styles.input}
              mode="outlined"
            />

            <Text style={styles.sectionTitle}>Modalidade</Text>
            <View style={styles.chipContainer}>
              {modalidades.map((modalidade) => (
                <Chip
                  key={modalidade}
                  selected={formData.modalidade === modalidade}
                  onPress={() => setFormData({...formData, modalidade})}
                  style={styles.chip}
                >
                  {modalidade}
                </Chip>
              ))}
            </View>

            <TextInput
              label="Capacidade M√°xima"
              value={formData.capacidade}
              onChangeText={(text) => setFormData({...formData, capacidade: text})}
              style={styles.input}
              mode="outlined"
              keyboardType="numeric"
            />

            <TextInput
              label="Hor√°rio"
              value={formData.horario}
              onChangeText={(text) => setFormData({...formData, horario: text})}
              style={styles.input}
              mode="outlined"
              placeholder="Ex: Segunda 19:00"
            />

            <TextInput
              label="Descri√ß√£o"
              value={formData.descricao}
              onChangeText={(text) => setFormData({...formData, descricao: text})}
              style={styles.input}
              mode="outlined"
              multiline
              numberOfLines={3}
            />

            <Divider style={styles.divider} />

            <View style={styles.buttonContainer}>
              <Button
                mode="outlined"
                onPress={() => navigation.goBack()}
                style={styles.cancelButton}
              >
                Cancelar
              </Button>

              <Button
                mode="contained"
                onPress={handleSubmit}
                style={styles.submitButton}
                buttonColor="#4CAF50"
              >
                Criar Aula
              </Button>
            </View>
          </Card.Content>
        </Card>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f8f9fa',
  },
  scrollView: {
    flex: 1,
  },
  card: {
    margin: ResponsiveUtils.spacing.md,
    borderRadius: ResponsiveUtils.borderRadius.large,
    ...ResponsiveUtils.elevation,
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: ResponsiveUtils.spacing.lg,
  },
  title: {
    marginLeft: ResponsiveUtils.spacing.md,
