              left={() => <List.Icon icon="bell" />}
              right={() => <List.Icon icon="chevron-right" />}
              onPress={() => Alert.alert('Info', 'Funcionalidade ser√° implementada')}
            />
            <Divider />

            <List.Item
              title="Privacidade"
              description="Configura√ß√µes de privacidade"
              left={() => <List.Icon icon="shield" />}
              right={() => <List.Icon icon="chevron-right" />}
              onPress={() => Alert.alert('Info', 'Funcionalidade ser√° implementada')}
            />
          </Card.Content>
        </Card>

        {/* A√ß√µes da Conta */}
        <Card style={styles.card}>
          <Card.Content>
            <Button 
              mode="outlined" 
              onPress={handleLogout}
              style={styles.logoutButton}
              icon="logout"
              textColor="#F44336"
            >
              Sair da Conta
            </Button>
          </Card.Content>
        </Card>
      </ScrollView>

      {/* Modal Treinos no Ano */}
      <Portal>
        <Modal
          visible={showYearModal}
          onDismiss={() => setShowYearModal(false)}
          contentContainerStyle={styles.modalContainer}
        >
          <Card style={styles.modalCard}>
            <Card.Title
              title="Treinos no ano"
              left={() => <Ionicons name="calendar" size={24} color="#2196F3" />}
              right={() => (
