import React, { useState } from 'react';
import { View, StyleSheet, ScrollView, Alert, Platform, Dimensions } from 'react-native';
import { 
  Card, 
  Text, 
  Button,
  ListItem,
  Switch,
  Icon,
  Divider
} from 'react-native-elements';
import { SafeAreaView } from 'react-native-safe-area-context';
import { useAuth } from '../../contexts/AuthContext';
import { ResponsiveUtils } from '../../utils/animations';

const SettingsScreen = ({ navigation }) => {
  const { user, userProfile, logout } = useAuth();
  const [notifications, setNotifications] = useState(true);
  const [darkMode, setDarkMode] = useState(false);
  const [autoBackup, setAutoBackup] = useState(true);

  const handleLogout = () => {
    Alert.alert(
      'Confirmar Logout',
      'Tem certeza que deseja sair da sua conta?',
      [
        { text: 'Cancelar', style: 'cancel' },
        { 
          text: 'Sair', 
          style: 'destructive',
          onPress: async () => {
            try {
              await logout();
            } catch (error) {
              Alert.alert('Erro', 'N√£o foi poss√≠vel fazer logout');
            }
          }
        }
      ]
    );
  };

  const handleChangePassword = () => {
    navigation.navigate('ChangePassword');
  };

  const handleDataExport = () => {
    Alert.alert('Em Desenvolvimento', 'Funcionalidade de exporta√ß√£o ser√° implementada em breve');
  };

  const handleDeleteAccount = () => {
    Alert.alert(
      'Excluir Conta',
      'Esta a√ß√£o √© irrevers√≠vel. Todos os seus dados ser√£o perdidos.',
      [
        { text: 'Cancelar', style: 'cancel' },
        { 
          text: 'Excluir', 
          style: 'destructive',
          onPress: () => {
            Alert.alert('Em Desenvolvimento', 'Funcionalidade ser√° implementada em breve');
          }
        }
      ]
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView 
        style={styles.scrollView}
        contentContainerStyle={styles.scrollContent}
        showsVerticalScrollIndicator={false}
      >
        {/* Informa√ß√µes da Conta */}
        <Card containerStyle={styles.card}>
          <View style={styles.cardHeader}>
            <Icon name="account-circle" type="material" size={24} color="#2196F3" />
            <Text h4 style={styles.cardTitle}>Conta</Text>
          </View>

          <ListItem bottomDivider>
            <Icon name="person" type="material" />
            <ListItem.Content>
              <ListItem.Title>Nome</ListItem.Title>
              <ListItem.Subtitle>{userProfile?.name || 'N√£o informado'}</ListItem.Subtitle>
            </ListItem.Content>
          </ListItem>

          <ListItem bottomDivider>
            <Icon name="email" type="material" />
            <ListItem.Content>
              <ListItem.Title>Email</ListItem.Title>
              <ListItem.Subtitle>{user?.email}</ListItem.Subtitle>
            </ListItem.Content>
          </ListItem>

          <ListItem bottomDivider onPress={() => navigation.navigate('Profile')}>
            <Icon name="edit" type="material" />
            <ListItem.Content>
              <ListItem.Title>Editar Perfil</ListItem.Title>
            </ListItem.Content>
            <ListItem.Chevron />
          </ListItem>

          <ListItem onPress={handleChangePassword}>
            <Icon name="lock" type="material" />
            <ListItem.Content>
              <ListItem.Title>Alterar Senha</ListItem.Title>
            </ListItem.Content>
            <ListItem.Chevron />
          </ListItem>
        </Card>

        {/* Prefer√™ncias */}
