#!/usr/bin/env node

// Script para criar o usu√°rio de teste necess√°rio para login
const { createUserWithEmailAndPassword, signInWithEmailAndPassword } = require('firebase/auth');
const { doc, setDoc, getDoc } = require('firebase/firestore');
const { auth, db } = require('../src/services/firebase');

async function createTestUser() {
  try {
    console.log('üöÄ Iniciando cria√ß√£o de usu√°rio de teste...\n');

    const testEmail = 'cti.maracau@ifce.edu.br';
    const testPassword = '123456';
    const userName = 'CTI Maracana√∫';

    console.log(`üìß Email: ${testEmail}`);
    console.log(`üîë Senha: ${testPassword}`);
    console.log(`üë§ Nome: ${userName}\n`);

    // Primeiro, verificar se o usu√°rio j√° existe tentando fazer login
    console.log('üîç Verificando se o usu√°rio j√° existe...');

    try {
      const existingUser = await signInWithEmailAndPassword(auth, testEmail, testPassword);
      console.log('‚úÖ Usu√°rio j√° existe e pode fazer login!');
      console.log(`üë§ UID: ${existingUser.user.uid}`);
      console.log(`üìß Email verificado: ${existingUser.user.emailVerified}`);

      // Verificar perfil no Firestore
      const userDoc = await getDoc(doc(db, 'usuarios', existingUser.user.uid));
      if (userDoc.exists()) {
        console.log('‚úÖ Perfil encontrado na cole√ß√£o "usuarios"');
        console.log('üìã Dados do perfil:', userDoc.data());
      } else {
        // Verificar na cole√ß√£o legacy
        const legacyDoc = await getDoc(doc(db, 'users', existingUser.user.uid));
        if (legacyDoc.exists()) {
          console.log('‚úÖ Perfil encontrado na cole√ß√£o "users" (legacy)');
          console.log('üìã Dados do perfil:', legacyDoc.data());
        } else {
          console.log('‚ö†Ô∏è Usu√°rio existe no Auth mas n√£o tem perfil no Firestore');
          console.log('üí° Criando perfil...');

          await setDoc(doc(db, 'usuarios', existingUser.user.uid), {
            name: userName,
            email: testEmail,
            tipo: 'admin', // Tipo de usu√°rio
            userType: 'admin', // Para compatibilidade
            isActive: true,
            createdAt: new Date(),
            updatedAt: new Date()
          });

          console.log('‚úÖ Perfil criado com sucesso!');
        }
      }

      return;
    } catch (loginError) {
      if (loginError.code === 'auth/invalid-credential' || 
          loginError.code === 'auth/user-not-found') {
        console.log('‚ùå Usu√°rio n√£o existe, criando...\n');
      } else {
        console.error('‚ùå Erro inesperado ao verificar usu√°rio:', loginError);
        return;
      }
    }

    // Criar o usu√°rio
    console.log('üÜï Criando nova conta...');
    const userCredential = await createUserWithEmailAndPassword(auth, testEmail, testPassword);
    const newUser = userCredential.user;

    console.log('‚úÖ Conta criada no Firebase Auth!');
    console.log(`üë§ UID: ${newUser.uid}`);
    console.log(`üìß Email: ${newUser.email}`);

    // Criar perfil no Firestore (nova estrutura)
    console.log('üìù Criando perfil no Firestore...');
    await setDoc(doc(db, 'usuarios', newUser.uid), {
      name: userName,
      email: testEmail,
      tipo: 'admin', // Tipo de usu√°rio - pode ser 'admin', 'instructor', 'student'
      userType: 'admin', // Para compatibilidade com c√≥digo legacy
      isActive: true,
      createdAt: new Date(),
      updatedAt: new Date(),
      // Campos espec√≠ficos para admin
      permissions: ['all'],
      academiaId: null // Ser√° definido quando associar a uma academia
    });

    console.log('‚úÖ Perfil criado no Firestore!');

    // Verificar se tudo foi criado corretamente
    console.log('\nüîç Verificando cria√ß√£o...');
    const userDoc = await getDoc(doc(db, 'usuarios', newUser.uid));
    if (userDoc.exists()) {
      console.log('‚úÖ Verifica√ß√£o bem-sucedida!');
      console.log('üìã Dados do perfil:', userDoc.data());
    }

    console.log('\nüéâ Usu√°rio de teste criado com sucesso!');
    console.log('üí° Agora voc√™ pode fazer login com:');
    console.log(`   üìß Email: ${testEmail}`);
    console.log(`   üîë Senha: ${testPassword}`);
    console.log(`   üë§ Tipo: Admin`);

  } catch (error) {
    console.error('‚ùå Erro ao criar usu√°rio:', error);
    console.error('üìù C√≥digo:', error.code);
    console.error('üìù Mensagem:', error.message);

    if (error.code === 'auth/email-already-in-use') {
      console.log('üí° Este email j√° est√° em uso. Tente fazer login normalmente.');
    } else if (error.code === 'auth/weak-password') {
      console.log('üí° A senha √© muito fraca. Use uma senha mais forte.');
    } else if (error.code === 'auth/invalid-email') {
      console.log('üí° O formato do email √© inv√°lido.');
    }
  }
}

// Executar se chamado diretamente
if (require.main === module) {
  createTestUser()
    .then(() => {
      console.log('\n‚ú® Script conclu√≠do!');
      process.exit(0);
    })
    .catch((error) => {
      console.error('\nüí• Erro fatal:', error);
      process.exit(1);
    });
}

module.exports = { createTestUser };

